import CommonService from "../../service/CommonService";
import DoctorAppointmentService from "../../service/front-desk/DoctorAppointmentService";
import FigureSetupService from "../../service/setup/FigureSetupService";
import AppointmentPrescriptionService from "../../service/doctor-panel/AppointmentPrescriptionService";
import MedicineSetupService from "../../service/setup/MedicineSetupService";
import TreatmentRulesSetupService from "../../service/setup/TreatmentRulesSetupService";
import TreatmentDurationSetupService from "../../service/setup/TreatmentDurationSetupService";
import TreatmentDoseSetupService from "../../service/setup/TreatmentDoseSetupService";
import PrescriptionReportService from "../../service/report/PrescriptionReportService";
import moment from "moment";
import { FilterMatchMode, FilterOperator } from "primevue/api";
import EmployeeSetupService from "../../service/setup/EmployeeSetupService";
import PatientRegistrationService from "../../service/front-desk/PatientRegistrationService";
import PatientReportService from "../../service/report/PatientReportService";
import PatientInvestigationRequestService from "../../service/front-desk/PatientInvestigationRequestService";
import DiagnosisReportService from "../../service/report/DiagnosisReportService";
import PatientProfileService from "../../service/profiles/PatientProfileService";
import DiagnosisSetupService from "../../service/setup/DiagnosisSetupService";

export default {
  data() {
    return {
      expandedRows: [],
      active: 0,
      investigationResultList: [],
      patient: {
        age_year: "0",
        age_month: "0",
        age_day: "0",
      },
      isAssistant: false,
      dataList: true,
      isHistory: false,
      isFemale: false,
      isAdvice: false,
      currentMedicationsDialog: false,
      pastMedicalHistoryDialog: false,
      onExaminationDialog: false,
      onExaminationPopupDialog: false,
      //editOnExaminationDialog: false,
      figureDialog: false,
      familyHistoryDialog: false,
      treatmentDialog: false,
      prevPrescriptionDialog: false,
      diagnosisList: [],
      currentMedicationsList: [],
      pastMedicalHistoryList: [],
      appoinmentStatusList: [],
      patientsRelativeList: [],
      doctorAppointmentList: [],
      prescriptionDataList: [],
      prevPrescriptionList: [],
      headerName: "Sections",
      tempData: [],
      patientHistory:{
        lstPersonalHistory:[],
        lstFamilyHistory:[],
        lstSystemsReview:[],
        lstSubstanceUse:[],
        lstReproductiveHistory:[],
      },
      currentMedications: {},
      pastMedicalHistory: {},
      appointmentInfo: {},
      onExamination: {},
      figure: {},
      familyHistory: {},
      treatmentSetup: {},
      doctorAppointment: {
        cdfromdate: moment(String(new Date())).format("MM/DD/YYYY"),
        cdtodate: moment(String(new Date())).format("MM/DD/YYYY"),
        // cdtodate: moment(
        //   String(new Date(new Date().setDate(new Date().getDate() + 7)))
        // ).format("MM/DD/YYYY"),
        appoinment_status: "Active",
      },

      //Test
      // doctorAppointment: {
      //   cdfromdate: "01/01/2021",
      //   cdtodate: "01/01/2022",
      //   appoinment_status: "All",
      // },

      appointmentPrescription: {
        cdprescription_date: moment(String(new Date())).format("MM/DD/YYYY"),
        cdfolowup_date: moment(
          String(new Date(new Date().setDate(new Date().getDate() + 7)))
        ).format("MM/DD/YYYY"),

        figure_show_in_prescription: true,
        familyHistory_show_in_prescription: true,
        pastHistory_show_in_prescription: true,
        allAdvice: false,

        lstAppoinmentPrescriptionCompliants: [],
        lstAppoinmentPrescriptionOnExamination: [],
        lstAppoinmentPrescriptionFigure: [],
        lstAppoinmentPrescriptionInvestigation: [],
        lstAppoinmentPrescriptionDiagnosis: [],
        lstAppoinmentPrescriptionFamilyHistory: [],
        lstAppoinmentPrescriptionPastHistory: [],
        lstNutrition: [],
        lstAppoinmentPrescriptionTreatment: [],
        lstAppoinmentPrescriptionAdvice: [],
      },
      medicineList: [],
      treatmentRulesList: [],
      treatmentDurationList: [],
      treatmentDoseList: [],
      // canvas
      canvas: null,
      context: null,
      isDrawing: false,
      startX: 0,
      startY: 0,
      points: [],
      //
      submitted: false,
      filters: {
        global: { value: null, matchMode: FilterMatchMode.CONTAINS },
        name: { value: null, matchMode: FilterMatchMode.STARTS_WITH },
      },
      initFilters() {
        this.filters = {
          global: { value: null, matchMode: FilterMatchMode.CONTAINS },
          name: {
            operator: FilterOperator.AND,
            constraints: [
              { value: null, matchMode: FilterMatchMode.STARTS_WITH },
            ],
          },
        };
      },
    };
  },
  created() {
    this.appointmentPrescriptionService = new AppointmentPrescriptionService();
    this.figureSetupService = new FigureSetupService();
    this.doctorAppointmentService = new DoctorAppointmentService();
    this.commonService = new CommonService();
    this.medicineSetupService = new MedicineSetupService();
    this.treatmentRulesSetupService = new TreatmentRulesSetupService();
    this.treatmentDurationSetupService = new TreatmentDurationSetupService();
    this.treatmentDoseSetupService = new TreatmentDoseSetupService();
    this.prescriptionReportService = new PrescriptionReportService();
    this.employeeSetupService = new EmployeeSetupService();
    this.patientRegistrationService = new PatientRegistrationService();
    this.patientReportService = new PatientReportService();
    this.patientInvestigationRequestService = new PatientInvestigationRequestService();
    this.diagnosisReportService = new DiagnosisReportService();
    this.patientProfileService = new PatientProfileService();
    this.diagnosisSetupService = new DiagnosisSetupService();

    this.userInfo = JSON.parse(localStorage.getItem("userInfo"));

    this.diagnosisSetupService
      .getAll()
      .then((data) => (this.diagnosisList = data.data));

    this.commonService
      .GetAllAppoinmentStatus()
      .then((data) => (this.appoinmentStatusList = data.data));

    this.commonService
      .GetAllPatientsRelatives()
      .then((data) => (this.patientsRelativeList = data.data));

    if (this.userInfo.doctor_id) {
      this.appointmentPrescriptionService
        .getPrescriptionDataByDoctorId(this.userInfo.doctor_id)
        .then((data) => (this.prescriptionDataList = data.data));

      this.doctor_id = this.userInfo.doctor_id;
      this.show();
    }
    if (this.userInfo.employee_id) {
      this.employeeSetupService
        .getById(this.userInfo.employee_id)
        .then((data) => this.employeeFormatted(data.data));
    }



    this.medicineSetupService
      .getActiveCombo()
      .then((data) => (this.medicineList = data.data));

    this.treatmentRulesSetupService
      .getActiveCombo()
      .then((data) => (this.treatmentRulesList = data.data));

    this.treatmentDurationSetupService
      .getActiveCombo()
      .then((data) => (this.treatmentDurationList = data.data));

    this.treatmentDoseSetupService
      .getActiveCombo()
      .then((data) => (this.treatmentDoseList = data.data));

    //this.show();
    this.newPrescription();

    let that = this;
    document.onkeyup = function (e) {
      if (e.ctrlKey && e.altKey && e.which == 67) {
        that.getPatientComplaints();
      } else if (e.ctrlKey && e.altKey && e.which == 69) {
        that.getOnExaminationsPopup();
        that.getOnExaminations();
      } else if (e.ctrlKey && e.altKey && e.which == 70) {
        that.getFigure();
      } else if (e.ctrlKey && e.altKey && e.which == 73) {
        that.getInvestigation();
      } else if (e.ctrlKey && e.altKey && e.which == 68) {
        that.getDiagnosis();
      } else if (e.ctrlKey && e.altKey && e.which == 72) {
        that.getFamilyHistory();
      } else if (e.ctrlKey && e.altKey && e.which == 80) {
        that.getPastHistory();
      } else if (e.ctrlKey && e.altKey && e.which == 84) {
        that.getTreatment();
      } else if (e.ctrlKey && e.altKey && e.which == 65) {
        that.getAdvice();
      }
      // else if (e.ctrlKey && e.altKey && e.shiftKey) {

      // }
    };

    var prescriptionData = JSON.parse(localStorage.getItem('prescriptionData'));
    if (prescriptionData) {
      this.prescription(prescriptionData);
      localStorage.removeItem('prescriptionData');
    }
  },
  methods: {
    employeeFormatted(data) {
      this.doctor_id = data.assistant_of_doctor_id;
      this.isAssistant = true;
      this.show();
    },
    reset() {
      this.doctorAppointment = {
        cdfromdate: moment(String(new Date())).format("MM/DD/YYYY"),
        cdtodate: moment(String(new Date())).format("MM/DD/YYYY"),
        // cdtodate: moment(
        //   String(new Date(new Date().setDate(new Date().getDate() + 7)))
        // ).format("MM/DD/YYYY"),
        appoinment_status: "Active",
      };

      // this.dataList = true;
      // this.submitted = false;
      this.show();
    },
    show() {
      this.submitted = true;
      if (!this.doctorAppointment.cdfromdate) {
        return;
      } else if (!this.doctorAppointment.cdtodate) {
        return;
      } else if (!this.doctorAppointment.appoinment_status) {
        return;
      } else {
        this.doctorAppointment.fromdate = moment(
          String(this.doctorAppointment.cdfromdate)
        ).format("YYYY-MM-DD");
        this.doctorAppointment.todate = moment(
          String(this.doctorAppointment.cdtodate)
        ).format("YYYY-MM-DD");
        this.doctorAppointmentService
          .getDynamicDoctorWise(
            this.doctorAppointment.fromdate,
            this.doctorAppointment.todate,
            this.doctorAppointment.appoinment_status,
            this.doctor_id
          )
          .then((data) => (this.doctorAppointmentList = data.data));
      }
      this.dataList = true;
      this.isHistory = false;
      this.submitted = false;
    },
    //
    isNameExists(list, column, value) {
      var result = false;
      list.forEach((element) => {
        if (element[column] == value) {
          result = true;
        }
      });
      if (result) {
        this.$toast.add({
          severity: "error",
          summary: "Error",
          detail: "Duplicate Data",
          life: 10000,
        });
      }
      return result;
    },
    newCurrentMedications() {
      this.currentMedicationsDialog = true;
    },
    async addCurrentMedications() {
      if (!this.currentMedications.medicine_id) {
        return;
      } else if (!this.currentMedications.treatment_rules_id) {
        return;
      } else if (!this.currentMedications.treatment_duration_id) {
        return;
      } else if (!this.currentMedications.treatment_dose_id) {
        return;
      } else if (!this.currentMedications.patient_id) {
        return;
      } else if (this.isNameExists(this.currentMedicationsList, 'medicine_id', this.currentMedications.medicine_id)) {
        return;
      } else {
        let result = await this.patientProfileService.currentMedicationsSave(this.currentMedications);
        if (result.status === 201) {
          this.getCurrentMedicationsByPatientId(this.currentMedications.patient_id);
          this.currentMedicationsDialog = false;
          this.$toast.add({
            severity: "success",
            summary: "Success",
            detail: "Saved Successfully",
            life: 10000,
          });
        } else {
          this.$toast.add({
            severity: "error",
            summary: "Failed",
            detail: result.res,
            life: 10000,
          });
        }
      }
    },
    confirmDeleteCurrentMedications(data) {
      this.$confirm.require({
        message: 'Do you want to delete this record?',
        header: 'Delete Confirmation',
        icon: 'pi pi-info-circle',
        acceptClass: 'p-button-danger',
        accept: () => {
          this.deleteCurrentMedications(data);
        },
        reject: () => {
          return;
        }
      });
    },
    async deleteCurrentMedications(data) {
      let result = await this.patientProfileService.currentMedicationsDelete(data);
      if (result.status === 202) {
        this.getCurrentMedicationsByPatientId(data.patient_id)
      } else {
        this.$toast.add({
          severity: "error",
          summary: "Failed",
          detail: result.res,
          life: 10000,
        });
      }
    },
    newPastMedicalHistory() {
      this.pastMedicalHistoryDialog = true;
    },
    async addPastMedicalHistory() {
      //this.pastMedicalHistory
      if (!this.pastMedicalHistory.diagnosis_id) {
        return;
      } else if (!this.pastMedicalHistory.patient_id) {
        return;
      } else if (this.isNameExists(this.pastMedicalHistoryList, 'diagnosis_id', this.pastMedicalHistory.diagnosis_id)) {
        return;
      } else {
        let result = await this.patientProfileService.pastHistorySave(this.pastMedicalHistory);
        if (result.status === 201) {
          this.getPastHistoryByPatientId(this.pastMedicalHistory.patient_id);
          this.pastMedicalHistoryDialog = false;
          this.$toast.add({
            severity: "success",
            summary: "Success",
            detail: "Saved Successfully",
            life: 10000,
          });
        } else {
          this.$toast.add({
            severity: "error",
            summary: "Failed",
            detail: result.res,
            life: 10000,
          });
        }
      }
    },
    confirmDeletePastMedicalHistory(data) {
      this.$confirm.require({
        message: 'Do you want to delete this record?',
        header: 'Delete Confirmation',
        icon: 'pi pi-info-circle',
        acceptClass: 'p-button-danger',
        accept: () => {
          this.deletePastMedicalHistory(data);
        },
        reject: () => {
          return;
        }
      });
    },
    async deletePastMedicalHistory(data) {
      let result = await this.patientProfileService.pastHistoryDelete(data);
      if (result.status === 202) {
        this.getPastHistoryByPatientId(data.patient_id)
      } else {
        this.$toast.add({
          severity: "error",
          summary: "Failed",
          detail: result.res,
          life: 10000,
        });
      }
    },
    hideHistory(){
      this.isHistory = false;
    },
    async patientHistorySave() {
      if (!this.patientHistory.patient_id) {
        return;
      } else {
        let result = await this.patientProfileService.patientHistorySave(this.patientHistory, this);
        if (result.status === 201) {
          this.hideHistory();
          this.$toast.add({
            severity: "success",
            summary: "Success",
            detail: "Saved Successfully",
            life: 10000,
          });
        } else {
          this.$toast.add({
            severity: "error",
            summary: "Failed",
            detail: result.res,
            life: 10000,
          });
        }
      }
    },
    historyCollection(data) {
      if (data.aPatient.gender == "Female") {
        this.isFemale = true;
      } else {
        this.isFemale = false;
      }
      this.currentMedications.patient_id = data.aPatient.patient_id;
      this.pastMedicalHistory.patient_id = data.aPatient.patient_id;
      this.patientHistory.patient_id = data.aPatient.patient_id;
      this.getCurrentMedicationsByPatientId(data.aPatient.patient_id);
      this.getPastHistoryByPatientId(data.aPatient.patient_id);
      this.patientProfileService.getSystemSubstance(data.aPatient.patient_id).then((data) => this.formattedSystemSubstance(data.data));
      this.patientRegistrationService.getById(data.aPatient.patient_id).then((data) => this.patientInfoFormatted(data.data));
      this.patientInvestigationRequestService.patientWise(data.aPatient.patient_id).then((data) => this.investigationResultList = data.data);

      this.isHistory = true;
    },
    formattedSystemSubstance(data){
      this.patientHistory.lstPersonalHistory = data.lstPersonalHistory;
      this.patientHistory.lstFamilyHistory = data.lstFamilyHistory;
      this.patientHistory.lstSystemsReview = data.lstSystemsReview;
      this.patientHistory.lstSubstanceUse = data.lstSubstanceUse;
      this.patientHistory.lstReproductiveHistory = data.lstReproductiveHistory;
    },
    getCurrentMedicationsByPatientId(patient_id) {
      this.patientProfileService.getCurrentMedicationsByPatientId(patient_id).then((data) => (this.currentMedicationsList = data.data));
    },
    getPastHistoryByPatientId(patient_id) {
      this.patientProfileService.getPastHistoryByPatientId(patient_id).then((data) => (this.pastMedicalHistoryList = data.data));
    },
    patientInfoFormatted(data) {
      this.patient = data;
      this.patient.dob = moment(String(data.dob)).format("MM/DD/YYYY");
    },
    formatDate2(value) {
      return moment(String(value)).format("MM/DD/YYYY");
    },
    reportPrint(barcode_or_level, template_name) {
      this.diagnosisReportService.GetInvestigationReportTemplateWise(barcode_or_level, template_name, this).then((data) => this.commonService.Downloadfile(data.data));
    },
    prescription(data) {
      this.appointmentInfo.appoinment_id = data.appoinment_id;
      this.appointmentInfo.patientReg = data.aPatient.registration_no;
      this.appointmentInfo.patientName =
        data.aPatient.first_name + " " + data.aPatient.last_name;
      this.appointmentInfo.patientGender = data.aPatient.gender;
      this.appointmentInfo.patientAgeYear = data.aPatient.age_year;
      this.appointmentInfo.patientAgeMonth = data.aPatient.age_month;
      this.appointmentInfo.patientAgeDay = data.aPatient.age_day;

      // var patientDOB = moment(String(data.aPatient.dob)).format(
      //   "MM/DD/YYYY"
      // );

      //this.appointmentInfo.age = Math.floor(moment(new Date()).diff(moment(patientDOB, "MM/DD/YYYY"), 'years', true));
      //this.appointmentInfo.age = moment(patientDOB, "MM/DD/YYYY").fromNow().split(" ")[0];

      this.dataList = false;
    },
    viewPatientProfile(patient_id) {
      localStorage.setItem('patientId_for_profile', patient_id);
      this.$router.push("/patient-profile");
    },
    previousPrescription(patient_id) {
      this.appointmentPrescriptionService
        .getPatientWisePrescriptionList(patient_id)
        .then((data) => (this.prevPrescriptionList = data.data));

      this.prevPrescriptionDialog = true;
    },
    downloadPrescription(appoinment_id) {
      this.prescriptionReportService.GetPescription(appoinment_id, this).then((data) => (this.commonService.Downloadfile(data.data)));
    },
    formatDate(value) {
      return moment(String(value)).format("MM/DD/YYYY h:mm:ss a");
    },
    checkDate(date) {
      var today = moment(String(new Date())).format("MM/DD/YYYY");
      var apptDate = moment(String(date)).format("MM/DD/YYYY");
      if (today == apptDate) {
        return true;
      } else {
        return false;
      }
    },

    //
    getPatientComplaints() {
      this.headerName = "Patient Complaints";
      this.filters['global'].value = "";
      this.isAdvice = false;
      this.tempData = [];
      this.prescriptionDataList.lstComplaint.forEach((element) => {
        this.tempData.push({
          name: element.compilants_name,
          id: element.compilants_id,
        });
      });
    },
    getOnExaminationsPopup() {
      this.popupOnExaminationsData = [];
      this.prescriptionDataList.lstOnExamination.forEach((element) => {
        this.popupOnExaminationsData.push({
          name: element.on_examination_name,
          id: element.on_examination_id,
          on_examination_result: 1,
        });
      });

      this.onExaminationPopupDialog = true;
    },
    getOnExaminations() {
      //this.getOnExaminationsPopup();
      this.headerName = "On Examinations";
      this.filters['global'].value = "";
      this.isAdvice = false;
      this.tempData = [];
      this.prescriptionDataList.lstOnExamination.forEach((element) => {
        this.tempData.push({
          name: element.on_examination_name,
          id: element.on_examination_id,
        });
      });
    },
    getFigure() {
      this.headerName = "Figure";
      this.filters['global'].value = "";
      this.isAdvice = false;
      this.tempData = [];
      this.prescriptionDataList.lstFigureSetup.forEach((element) => {
        this.tempData.push({
          name: element.figure_name,
          id: element.figure_id,
        });
      });
    },
    getInvestigation() {
      this.headerName = "Investigation";
      this.filters['global'].value = "";
      this.isAdvice = false;
      this.tempData = [];
      this.prescriptionDataList.lstInvestigation.forEach((element) => {
        this.tempData.push({
          name: element.investingation_name,
          id: element.investigation_id,
        });
      });
    },
    getDiagnosis() {
      this.headerName = "Diagnosis";
      this.filters['global'].value = "";
      this.isAdvice = false;
      this.tempData = [];
      this.prescriptionDataList.lstDiagnosis.forEach((element) => {
        this.tempData.push({
          name: element.diagnosis_name,
          id: element.diagnosis_id,
        });
      });
    },
    getFamilyHistory() {
      this.headerName = "Family History";
      this.filters['global'].value = "";
      this.isAdvice = false;
      this.tempData = [];
      this.prescriptionDataList.lstDiagnosis.forEach((element) => {
        this.tempData.push({
          name: element.diagnosis_name,
          id: element.diagnosis_id,
        });
      });
    },
    getPastHistory() {
      this.headerName = "Past History";
      this.filters['global'].value = "";
      this.isAdvice = false;
      this.tempData = [];
      this.prescriptionDataList.lstDiagnosis.forEach((element) => {
        this.tempData.push({
          name: element.diagnosis_name,
          id: element.diagnosis_id,
        });
      });
    },
    getTreatment() {
      this.headerName = "Medicine";
      this.filters['global'].value = "";
      this.isAdvice = false;
      this.tempData = [];
      this.prescriptionDataList.lstTreatment.forEach((element) => {
        this.tempData.push({
          _medicine_type_name: element._medicine_type_name,
          _medicine_name: element._medicine_name,
          name: element._medicine_type_name + " " + element._medicine_name + " " + element._treatment_dose_name + " " + element._treatment_rule_name + " " + element._treatment_duration_name,
          id: element.medicine_id,
          _treatment_dose_name: element._treatment_dose_name,
          treatment_dose_id: element.treatment_dose_id,
          _treatment_rule_name: element._treatment_rule_name,
          treatment_rules_id: element.treatment_rules_id,
          _treatment_duration_name: element._treatment_duration_name,
          treatment_duration_id: element.treatment_duration_id,
          description: element.description,
        });
      });
    },
    getAdvice() {
      this.headerName = "Advice";
      this.filters['global'].value = "";
      this.isAdvice = true;
      this.tempData = [];
      this.prescriptionDataList.lstAdvices.forEach((element) => {
        this.tempData.push({
          name: element.advice_name,
          id: element.advice_id,
        });
      });
    },
    addOnExaminationFromPopup() {
      //this.submitted = true;
      //this.requred = "";
      this.popupOnExaminationsData.forEach((element) => {
        if (element.on_examination_result) {
          //this.requred = "required";
          var aOnExaminations =
            this.appointmentPrescription.lstAppoinmentPrescriptionOnExamination.filter(
              function (el) {
                return el.on_examination_id === element.id;
              },
              this
            );

          if (!aOnExaminations.length) {
            this.appointmentPrescription.lstAppoinmentPrescriptionOnExamination.push({
              name: element.name,
              on_examination_id: element.id,
              on_examination_result:
                element.on_examination_result.toString(),
            });
          }
        }
      });

      // if (this.requred) {
      //   this.$toast.add({
      //     severity: "error",
      //     summary: "Required",
      //     detail: "Please fill in the required fields",
      //     life: 10000,
      //   });
      //   return;
      // }

      this.onExaminationPopupDialog = false;
    },
    // add all advice checkbox
    addAllAdvice() {
      if (!this.appointmentPrescription.allAdvice) {
        this.tempData.forEach((element) => {
          var aAdvice =
            this.appointmentPrescription.lstAppoinmentPrescriptionAdvice.filter(
              function (el) {
                return el.advice_id === element.id;
              },
              this
            );

          if (!aAdvice.length) {
            this.appointmentPrescription.lstAppoinmentPrescriptionAdvice.push({
              name: element.name,
              advice_id: element.id,
            });
          }
        });
      }
    },
    // add value 
    getValue(data) {
      if (this.headerName == "Patient Complaints") {
        var aCompliants =
          this.appointmentPrescription.lstAppoinmentPrescriptionCompliants.filter(
            function (el) {
              return el.compilants_id === data.id;
            },
            this
          );

        if (!aCompliants.length) {
          this.appointmentPrescription.lstAppoinmentPrescriptionCompliants.push(
            {
              name: data.name,
              compilants_id: data.id,
            }
          );
        }
      } else if (this.headerName == "On Examinations") {
        var aOnExaminations =
          this.appointmentPrescription.lstAppoinmentPrescriptionOnExamination.filter(
            function (el) {
              return el.on_examination_id === data.id;
            },
            this
          );

