    async add() {
      try {
        if (
          !this.appointmentPrescription.lstAppoinmentPrescriptionCompliants
            .length
        ) {
          this.$toast.add({
            severity: "error",
            summary: "Error",
            detail: "Please add Patient Complaints",
            life: 10000,
          });
          return;
        } else if (
          !this.appointmentPrescription.lstAppoinmentPrescriptionOnExamination
            .length
        ) {
          this.$toast.add({
            severity: "error",
            summary: "Error",
            detail: "Please add On Examination",
            life: 10000,
          });
          return;
        } else {
          this.appointmentPrescription.appoinment_id =
            this.appointmentInfo.appoinment_id;

          this.appointmentPrescription.prescription_date = moment(
            String(this.appointmentPrescription.cdprescription_date)
          ).format("YYYY-MM-DD");

          if (this.appointmentPrescription.cdfolowup_date) {
            this.appointmentPrescription.folowup_date = moment(
              String(this.appointmentPrescription.cdfolowup_date)
            ).format("YYYY-MM-DD");
          }

          let result = await this.appointmentPrescriptionService.add(
            this.appointmentPrescription, this
          );
          if (result.status === 200) {
            this.downloadPrescription(this.appointmentPrescription.appoinment_id)
            this.$toast.add({
              severity: "success",
              summary: "Success",
              detail: "Prescribed Successfully",
              life: 10000,
            });
            this.newPrescription();
            this.reset();
          } else {
            this.$toast.add({
              severity: "error",
              summary: "Failed",
              detail: result.res,
              life: 10000,
            });
          }
        }
      } catch (error) {
        this.$toast.add({
          severity: "error",
          summary: "Failed",
          detail: error,
          life: 10000,
        });
      }
    },
    newPrescription() {
      this.headerName = "Sections";
      this.filters['global'].value = "";
      this.tempData = [];
      this.onExamination = {};
      this.figure = {};
      this.familyHistory = {};
      this.appointmentPrescription = {
