import DoctorBillingService from "../../service/cash-counter/DoctorBillingService";
import IPDReportService from "../../service/report/IPDReportService";
import CommonService from "../../service/CommonService";
// Assign Bill
import ConsultancyDepartmentSetupService from "../../service/setup/ConsultancyDepartmentSetupService";
import DoctorSetupService from "../../service/setup/DoctorSetupService";
import DoctorAssistantSetupService from "../../service/setup/DoctorAssistantSetupService";
import moment from "moment";
import { FilterMatchMode, FilterOperator } from "primevue/api";

export default {
    data() {
        return {
            admission_no: "",
            doctorBilling: {
                discount_amount: 0,
                discount_percent: 0,
                doctorBillingDetails: [],
            },
            
            serviceInfoDialog: false,
            consultancyDepartmentList: [],
            doctorList: [],
            assistantList: [],
            typeList: [
                { text: "Consultation", value: "Consultation" },
                { text: "Surgical Team", value: "Surgical Team" },
            ],
            assign: {
                assignDoctor: {},
                assignDoctorAssistant: {},
            },
            filters: {
                global: { value: null, matchMode: FilterMatchMode.CONTAINS },
                name: { value: null, matchMode: FilterMatchMode.STARTS_WITH },
            },
            initFilters() {
                this.filters = {
                    global: { value: null, matchMode: FilterMatchMode.CONTAINS },
                    name: {
                        operator: FilterOperator.AND,
                        constraints: [
                            { value: null, matchMode: FilterMatchMode.STARTS_WITH },
                        ],
                    },
                };
            },
            submitted: false,
        };
    },
    created() {
        this.doctorBillingService = new DoctorBillingService();
        this.iPDReportService = new IPDReportService();
        this.commonService = new CommonService();
        // Assign Bill
        this.doctorSetupService = new DoctorSetupService();
        this.consultancyDepartmentSetupService = new ConsultancyDepartmentSetupService();
        this.doctorAssistantSetupService = new DoctorAssistantSetupService();

        this.consultancyDepartmentSetupService
            .getActiveCombo()
            .then((data) => (this.consultancyDepartmentList = data.data));

        this.doctorSetupService
            .getAll()
            .then((data) => this.mergeDoctorInfo(data.data));

        this.doctorAssistantSetupService
            .getAll()
            .then((data) => (this.assistantList = data.data));
    },
    methods: {
        receiptPrint(id) {
            this.iPDReportService.GetDoctorBilling(id, this).then((data) => (this.commonService.Downloadfile(data.data)));
        },
        reset() {
            this.submitted = false;
            this.admission_no = "";
            this.doctorBilling = {
                discount_amount: 0,
                discount_percent: 0,
                doctorBillingDetails: [],
            };
            
            this.assign = {
                assignDoctor: {},
                assignDoctorAssistant: {},
            }
        },
        hideDialog() {
            this.submitted = false;
            this.serviceInfoDialog = false;
            this.assign = {
                assignDoctor: {},
                assignDoctorAssistant: {},
            }
        },
        mergeDoctorInfo(data) {
            this.doctorList = [];
            data.forEach((element) => {
                var obj = element;
                obj.doctor_degree = "";
                obj.lstConsultantDegree.forEach((el) => {
                    obj.doctor_degree += el.consultant_degree_name + ". ";
                });

                obj.doctor_name = obj.doctor_degree + " " + element.doctor_name;
                this.doctorList.push(obj);
            });
        },
        filterDoctorsDepartmentWise() {
            if (this.assign.assignDoctor.ddldepartment) {
                return this.doctorList.filter(function (el) {
                    return el.department_id === this.assign.assignDoctor.ddldepartment.value;
                }, this);
            }
        },
        mergeAssistantInfo(data) {
            this.assistantList = [];
            data.forEach((element) => {
                var obj = element;
                obj.doctor_assistant_name = element.doctor_assistant_name;
                this.assistantList.push(obj);
            });
        },
        getFormatted(obj) {
            if (obj.doctorBillingInfo) {
                var doctorBillingInfo = JSON.parse(obj.doctorBillingInfo);
                var doctorBillingDetails = JSON.parse(obj.doctorBillingDetails);

                this.doctorBilling.first_name = doctorBillingInfo[0].first_name + " " + doctorBillingInfo[0].last_name;
                this.doctorBilling.admission_id = doctorBillingInfo[0].admission_id;
                this.doctorBilling.contact_no = doctorBillingInfo[0].contact_no;
                this.doctorBilling.admission_date = this.formatDate(doctorBillingInfo[0].admission_date);
                this.doctorBilling.doctorBillingDetails = doctorBillingDetails;

                this.gridSum();
            } else {
                this.$toast.add({
                    severity: "warn",
                    summary: "Warn Message",
                    detail: obj,
                    life: 10000,
                });
                this.reset()
            }
        },
        getDoctorBillingData() {
            this.doctorBillingService
                .getDoctorBillingData(this.admission_no, this)
                .then((data) => this.getFormatted(data.data));
        },
        formatDate(value) {
            return moment(String(value)).format("MM/DD/YYYY");
        },
        gridSum() {
            var total = 0;
            this.doctorBilling.doctorBillingDetails.forEach((element) => {
                total += parseFloat(element.net_amount);
            });
            this.doctorBilling.total = total;

            this.doctorBilling.net_total = this.doctorBilling.total - this.doctorBilling.discount_amount;
        },
        newServiceInfo() {
            if (this.doctorBilling.admission_id) {
                this.serviceInfoDialog = true;
            } else {
                this.$toast.add({
                    severity: "error",
                    summary: "Error Message",
                    detail: "Admission info required",
                    life: 10000,
                });
            }
        },
        addAssignBill() {
            // Initialize maxId with the minimum possible value
            let maxId = Number.MIN_SAFE_INTEGER;

            // Iterate over the list and update maxId if a higher value is found
            for (let i = 0; i < this.doctorBilling.doctorBillingDetails.length; i++) {
                const item = this.doctorBilling.doctorBillingDetails[i];
                if (item.auto_id > maxId) {
                    maxId = item.auto_id;
                }
            }
            if (this.assign.type == 'Doctor') {
                this.submitted = true;
                if (!this.assign.assignDoctor.ddldepartment) {
                    return;
                } else if (!this.assign.assignDoctor.ddldoctor) {
                    return;
                } else if (!this.assign.assignDoctor.type) {
                    return;
                } else if (!this.assign.assignDoctor.fee) {
                    return;
                } else {
                    this.doctorBilling.doctorBillingDetails.push({
                        auto_id: maxId + 1,
                        admission_id: this.doctorBilling.admission_id,
                        ref_id: this.assign.assignDoctor.ddldoctor.doctor_id,
                        fee: this.assign.assignDoctor.fee,
                        discount_percent: 0,
                        discount_amount: 0,
                        net_amount: this.assign.assignDoctor.fee,
                        type: this.assign.assignDoctor.type,
                        ref_type: this.assign.type,
                        description: this.assign.assignDoctor.description,
                        name: this.assign.assignDoctor.ddldoctor.doctor_name + " (" + this.assign.assignDoctor.ddldepartment.text + ")",
                    });
                    this.hideDialog();
                    this.gridSum();
                }
            }
            else if (this.assign.type == 'Assistant') {
                this.submitted = true;
                if (!this.assign.assignDoctorAssistant.doctor_ddlassistant) {
                    return;
                } else if (!this.assign.assignDoctorAssistant.type) {
                    return;
                } else if (!this.assign.assignDoctorAssistant.fee) {
                    return;
                } else {
                    this.doctorBilling.doctorBillingDetails.push({
                        auto_id: maxId + 1,
                        admission_id: this.doctorBilling.admission_id,
                        ref_id: this.assign.assignDoctorAssistant.doctor_ddlassistant.doctor_assistant_id,
                        fee: this.assign.assignDoctorAssistant.fee,
                        discount_percent: 0,
                        discount_amount: 0,
                        net_amount: this.assign.assignDoctorAssistant.fee,
                        type: this.assign.assignDoctorAssistant.type,
                        ref_type: this.assign.type,
                        description: this.assign.assignDoctorAssistant.description,
                        name: this.assign.type + " " + this.assign.assignDoctorAssistant.doctor_ddlassistant.doctor_assistant_name,
                    });
                    this.hideDialog();
                    this.gridSum();
                }
            }
        },
        async add() {
            this.submitted = true;
            try {
                this.doctorBilling.doctorBillingDetails = this.doctorBilling.doctorBillingDetails.filter(function (elm) {
                    return elm.fee != 0;
                }, this);
                if (this.doctorBilling.doctorBillingDetails.length == 0) {
                    this.$toast.add({
                        severity: "error",
                        summary: "Error Message",
                        detail: "Billing data required",
                        life: 10000,
                    });
                    return;
                } else {
                    // this.doctorBilling.doctorBillingDetails.forEach((element) => {
                    //     element.admission_id = this.doctorBilling.admission_id;
                    // });
                    let result = await this.doctorBillingService.add(
                        this.doctorBilling, this
                    );
                    if (result.status === 200) {
                        this.receiptPrint(result.data)
                        this.$toast.add({
                            severity: "success",
                            summary: "Success",
                            detail: "Pay Successfully",
                            life: 10000,
                        });
                        this.reset();
                    } else if (result.status === 409) {
                        this.$toast.add({
                            severity: "error",
                            summary: "Error",
                            detail: result.data,
                            life: 10000,
                        });
                        this.reset();
                    } else {
                        this.$toast.add({
                            severity: "error",
                            summary: "Failed",
                            detail: result.res,
                            life: 10000,
                        });
                        this.reset();
                    }
                }
            } catch (error) {
                this.$toast.add({
                    severity: "error",
                    summary: "Failed",
                    detail: error,
                    life: 10000,
                });
                this.reset();
            }
        },
        calculateNetFee(data){
            this.doctorBilling.doctorBillingDetails.forEach(
                (element) => {
                    if (element.auto_id == data.auto_id) {
                        element.discount_amount = 0;
                        element.discount_percent
                        element.net_amount = element.fee;
                    }
                }
            );
            this.gridSum();
        },
        // line discount
        calculatePerDiscountAmount(data) {
            this.doctorBilling.doctorBillingDetails.forEach(
                (element) => {
                    if (element.auto_id == data.auto_id) {
                        element.discount_amount = (element.fee * element.discount_percent) / 100;
                        element.net_amount = element.fee - element.discount_amount;
                    }
                }
            );
            this.gridSum();
        },
        calculatePerDiscountPercent(data) {
            this.doctorBilling.doctorBillingDetails.forEach(
                (element) => {
                    if (element.auto_id == data.auto_id) {
                        element.discount_percent = parseFloat((element.discount_amount / element.fee) * 100).toFixed(2);
                        //var discount_amount = (element.charge * element.discount_percent) / 100;
                        element.net_amount = element.fee - element.discount_amount;
                    }
                }
            );
            this.gridSum();
        },
        // line discount
        calculateDiscountAmount() {
            this.doctorBilling.discount_amount = (this.doctorBilling.total * this.doctorBilling.discount_percent) / 100;
            //this.doctorBilling.net_total = this.doctorBilling.total - this.doctorBilling.discount_amount;
            this.gridSum();
        },
        calculateDiscountPercent() {
            this.doctorBilling.discount_percent = parseFloat((this.doctorBilling.discount_amount / this.doctorBilling.total) * 100).toFixed(2);
            //this.doctorBilling.net_total = this.doctorBilling.total - this.doctorBilling.discount_amount;
            this.gridSum();
        },

        async preview() {
            this.submitted = true;
            try {
                // this.doctorBilling.doctorBillingDetails = this.doctorBilling.doctorBillingDetails.filter(function (elm) {
                //     return elm.fee != 0;
                // }, this);
                if (this.doctorBilling.doctorBillingDetails.length == 0) {
                    this.$toast.add({
                        severity: "error",
                        summary: "Error Message",
                        detail: "Billing data required",
                        life: 10000,
                    });
                    return;
                } else {
                    let result = await this.doctorBillingService.preview(
                        this.doctorBilling, this
                    );
                    if (result.status === 200) {
                        this.tempReceiptPrint(result.data);
                        console.log("Successfully preview");
                    } else if (result.status === 409) {
                        this.$toast.add({
                            severity: "error",
                            summary: "Error",
                            detail: result.data,
                            life: 10000,
                        });
                    } else {
                        this.$toast.add({
                            severity: "error",
                            summary: "Failed",
                            detail: result.res,
                            life: 10000,
                        });
                    }
                }
            } catch (error) {
                this.$toast.add({
                    severity: "error",
                    summary: "Failed",
                    detail: error,
                    life: 10000,
                });
            }
        },

        tempReceiptPrint(id) {
            this.iPDReportService.GetDoctorBillingTemp(id, this).then((data) => (this.commonService.Downloadfile(data.data)));
        },
    },
};
