import CommonService from "../../service/CommonService";
import OTScheduleService from "../../service/ot/OTScheduleService";
import OTReportService from "../../service/report/OTReportService";
// Assign
import ConsultancyDepartmentSetupService from "../../service/setup/ConsultancyDepartmentSetupService";
import DoctorSetupService from "../../service/setup/DoctorSetupService";
import DoctorAssistantSetupService from "../../service/setup/DoctorAssistantSetupService";
import DepartmentSetupService from "../../service/setup/DepartmentSetupService";
import NurseSetupService from "../../service/setup/NurseSetupService";
// Service & Package
// import ServiceSetupService from "../../service/setup/ServiceSetupService";
// import ServicePackageSetupService from "../../service/setup/ServicePackageSetupService";

import moment from "moment";
import { FilterMatchMode, FilterOperator } from "primevue/api";

export default {
    data() {
        return {
            dataList: false,
            otStatusList: [],
            otScheduleList: [],
            assignedList: [],
            isAssigned: true,
            otSchedule: {
                cdfromdate: moment(String(new Date())).format("MM/DD/YYYY"),
                cdtodate: moment(String(new Date())).format("MM/DD/YYYY"),
                // cdtodate: moment(
                //   String(new Date(new Date().setDate(new Date().getDate() + 7)))
                // ).format("MM/DD/YYYY"),
                ot_status: "All",
            },
            submitted: false,
            filters: {
                global: { value: null, matchMode: FilterMatchMode.CONTAINS },
                name: { value: null, matchMode: FilterMatchMode.STARTS_WITH },
            },
            initFilters() {
                this.filters = {
                    global: { value: null, matchMode: FilterMatchMode.CONTAINS },
                    name: {
                        operator: FilterOperator.AND,
                        constraints: [
                            { value: null, matchMode: FilterMatchMode.STARTS_WITH },
                        ],
                    },
                };
            },
            // Assign
            otAssignedInfoDialog: false,
            otAssignDialog: false,
            consultancyDepartmentList: [],
            departmentList: [],
            doctorList: [],
            assistantList: [],
            nurseList: [],
            assign: {
                otAssignDoctor: {},
                otAssignDoctorAssistant: {},
                otAssignNurse: {},
            },
            // Status update
            updateStatusDialog: false,
            updateStatusList: [
                { text: "Start", value: "Start" },
                { text: "End", value: "End" },
                { text: "Canceled", value: "Canceled" },
            ],
            statusUpdate: {

            },
            // OT Note
            updateNoteDialog: false,
            noteUpdate: {}
        };
    },
    created() {
        this.otScheduleService = new OTScheduleService();
        this.otReportService = new OTReportService();
        this.commonService = new CommonService();
        // Assign
        this.doctorSetupService = new DoctorSetupService();
        this.consultancyDepartmentSetupService = new ConsultancyDepartmentSetupService();
        this.doctorAssistantSetupService = new DoctorAssistantSetupService();
        this.departmentSetupService = new DepartmentSetupService();
        this.nurseSetupService = new NurseSetupService();
        // Service & Package
        // this.serviceSetupService = new ServiceSetupService();
        // this.servicePackageSetupService = new ServicePackageSetupService();

        this.consultancyDepartmentSetupService
            .getActiveCombo()
            .then((data) => (this.consultancyDepartmentList = data.data));

        this.departmentSetupService
            .getAll()
            .then((data) => (this.departmentList = data.data));

        this.commonService
            .GetAllOTStatus()
            .then((data) => (this.otStatusList = data.data));

        this.show();
    },
    methods: {
        reset() {
            this.otSchedule = {
                cdfromdate: moment(String(new Date())).format("MM/DD/YYYY"),
                cdtodate: moment(String(new Date())).format("MM/DD/YYYY"),
                // cdtodate: moment(
                //   String(new Date(new Date().setDate(new Date().getDate() + 7)))
                // ).format("MM/DD/YYYY"),
                ot_status: "All",
            };
            this.dataList = false;
            this.submitted = false;
            this.show();
        },
        show() {
            this.submitted = true;
            if (!this.otSchedule.cdfromdate) {
                return;
            } else if (!this.otSchedule.cdtodate) {
                return;
            } else if (!this.otSchedule.ot_status) {
                return;
            } else {
                this.otSchedule.fromdate = moment(
                    String(this.otSchedule.cdfromdate)
                ).format("YYYY-MM-DD");
                this.otSchedule.todate = moment(
                    String(this.otSchedule.cdtodate)
                ).format("YYYY-MM-DD");
                this.otScheduleService
                    .getDynamic(
                        this.otSchedule.fromdate,
                        this.otSchedule.todate,
                        this.otSchedule.ot_status
                    )
                    .then((data) => (this.otScheduleList = data.data));
            }
            this.dataList = true;
            this.submitted = false;
        },
        formatDate(value) {
            return moment(String(value)).format("MM/DD/YYYY");
        },
        formatDateTime(value) {
            return moment(String(value)).format("MM/DD/YYYY h:mm:ss a");
        },

        // reSchedule(data) {
        //   localStorage.setItem('ot_schedule_no_for_reschedule', data);
        //   this.$router.push("/ot-form");
        // },

        showOTAssignedInfo(data) {
            this.assign.ot_schedule_id = data.ot_schedule_id;
            var ot_start_time = moment(
                String(data.ot_start_time)
            ).format("YYYY-MM-DD HH:mm");
            var ot_end_time = moment(
                String(data.ot_end_time)
            ).format("YYYY-MM-DD HH:mm");
            this.doctorSetupService.getOTAssignEligibleDoctors(ot_start_time, ot_end_time).then((data) => this.mergeDoctorInfo(data.data));
            this.nurseSetupService.getOTAssignEligibleNurses(ot_start_time, ot_end_time).then((data) => this.mergeNurseInfo(data.data));
            this.doctorAssistantSetupService.getOTAssignEligibleDoctorAssistants(ot_start_time, ot_end_time).then((data) => this.mergeAssistantInfo(data.data));

            if (data.ot_status == "Created" || data.ot_status == "Start") {
                this.isAssigned = true;
            } else {
                this.isAssigned = false;
            }

            this.otScheduleService.getOTAssignedList(data.ot_schedule_id).then((data) => this.formattedAssignedList(data.data));
        },
        mergeDoctorInfo(data) {
            this.doctorList = [];
            data.forEach((element) => {
                var obj = element;
                obj.doctor_degree = "";
                obj.lstConsultantDegree.forEach((el) => {
                    obj.doctor_degree += el.consultant_degree_name + ". ";
                });

                obj.doctor_name = element.doc_no + " - (" + obj.doctor_degree + ") - " + element.doctor_name;
                this.doctorList.push(obj);
            });
        },
        filterDoctorsDepartmentWise() {
            return this.doctorList.filter(function (el) {
                return el.department_id === this.assign.otAssignDoctor.department_id;
            }, this);
        },
        mergeNurseInfo(data) {
            this.nurseList = [];
            data.forEach((element) => {
                var obj = element;
                obj.nurse_name = element.nurse_no + " - (" + obj._designation_name + ") - " + element.first_name + " " + element.last_name;
                this.nurseList.push(obj);
            });
        },
        filterNursesDepartmentWise() {
            return this.nurseList.filter(function (el) {
                return el.department_id === this.assign.otAssignNurse.ndepartment_id;
            }, this);
        },
        mergeAssistantInfo(data) {
            this.assistantList = [];
            data.forEach((element) => {
                var obj = element;
                obj.doctor_assistant_name = element.doctor_assistant_name + " " + element.contact_no;
                this.assistantList.push(obj);
            });
        },
        formattedAssignedList(data) {
            this.assignedList = data;
            this.otAssignedInfoDialog = true;
        },
        // Assign
        otAssignInfo() {
            this.otAssignedInfoDialog = false;
            this.otAssignDialog = true;
        },
        async addAssign() {
            this.submitted = true;
            try {
                if (this.assign.type == "Doctor" && !this.assign.otAssignDoctor.department_id && !this.assign.otAssignDoctor.doctor_id) {
                    return;
                } else if (this.assign.type == "Nurse" && !this.assign.otAssignNurse.ndepartment_id && !this.assign.otAssignNurse.nurse_id) {
                    return;
                } else if (this.assign.type == "Assistant" && !this.assign.otAssignDoctorAssistant.doctor_assistant_id) {
                    return;
                } else {
                    let result = await this.otScheduleService.otAssignSave(
                        this.assign, this
                      );
            
                      if (result.status === 201) {
                        this.$toast.add({
                          severity: "success",
                          summary: "Success",
                          detail: "Assigned Successfully",
                          life: 10000,
                        });
                        this.hideDialog();
                      } else {
                        this.$toast.add({
                          severity: "error",
                          summary: "Failed",
                          detail: result.res,
                          life: 10000,
                        });
                      }
                }
            } catch (error) {
                this.$toast.add({
                    severity: "error",
                    summary: "Failed",
                    detail: error,
                    life: 10000,
                });
            }
        },
        updateStatusDialogShow(data){
            this.statusUpdate = {};
            this.statusUpdate.ot_schedule_id = data.ot_schedule_id;
            this.updateStatusDialog = true;
        },
        async updateStatus(){
            this.submitted = true;
            try {
                if (!this.statusUpdate.ot_schedule_id) {
                    return;
                } else if (!this.statusUpdate.ot_status) {
                    return;
                } else {
                    let result = await this.otScheduleService.otStatusUpdate(
                        this.statusUpdate, this
                      );
            
                      if (result.status === 201) {
                        this.$toast.add({
                          severity: "success",
                          summary: "Success",
                          detail: "Updated Successfully",
                          life: 10000,
                        });
                        this.show();
                        this.hideDialog();
                      } else {
                        this.$toast.add({
                          severity: "error",
                          summary: "Failed",
                          detail: result.res,
                          life: 10000,
                        });
                      }
                }
            } catch (error) {
                this.$toast.add({
                    severity: "error",
                    summary: "Failed",
                    detail: error,
                    life: 10000,
                });
            }
        },
        hideDialog() {
            this.otAssignDialog = false;
            this.assign = {
                otAssignDoctor: {},
                otAssignDoctorAssistant: {},
                otAssignNurse: {},
            };

            this.updateStatusDialog = false;
            this.statusUpdate = {

            }
            this.submitted = false;
            
            this.updateNoteDialog = false;
            this.noteUpdate = {};
        },
        detailsPrint(ot_schedule_id){
            this.otReportService.GetOTScheduleDetails(ot_schedule_id, this).then((data) => (this.commonService.Downloadfile(data.data)));
        },
        getNote(data){
            this.otScheduleService.getOTNoteByScheduleId(data).then((data) => this.formattedNote(data.data));
        },
        formattedNote(data){
            this.noteUpdate = data;
            this.updateNoteDialog = true;
        },
        async updateNote(){
            this.submitted = true;
            try {
                if (!this.noteUpdate.ot_schedule_id) {
                    return;
                } else {
                    let result = await this.otScheduleService.oTNoteUpdate(
                        this.noteUpdate, this
                      );
            
                      if (result.status === 201) {
                        this.$toast.add({
                          severity: "success",
                          summary: "Success",
                          detail: "Updated Successfully",
                          life: 10000,
                        });
                        this.show();
                        this.hideDialog();
                      } else {
                        this.$toast.add({
                          severity: "error",
                          summary: "Failed",
                          detail: result.res,
                          life: 10000,
                        });
                      }
                }
            } catch (error) {
                this.$toast.add({
                    severity: "error",
                    summary: "Failed",
                    detail: error,
                    life: 10000,
                });
            }
        },
    }
};
