import PatientInvestigationRequestService from "../../service/front-desk/PatientInvestigationRequestService";
import PatientRegistrationService from "../../service/front-desk/PatientRegistrationService";
import PatientReportService from "../../service/report/PatientReportService";
import CommonService from "../../service/CommonService";
import DesignationSetupService from "../../service/setup/DesignationSetupService";
import ConsultancyDepartmentSetupService from "../../service/setup/ConsultancyDepartmentSetupService";
import DoctorSetupService from "../../service/setup/DoctorSetupService";
import EmployeeSetupService from "../../service/setup/EmployeeSetupService";
import BuildingSetupService from "../../service/setup/BuildingSetupService";
import RoomSetupService from "../../service/setup/RoomSetupService";
import ReferredBySetupService from "../../service/setup/ReferredBySetupService";
import InvestigationSetupService from "../../service/setup/InvestigationSetupService";
import ProductSetupService from "../../service/setup/ProductSetupService";
//import CollectionCenterSetupService from "../../service/setup/CollectionCenterSetupService";
import Validation from "../../scripts/validation";
import AppointmentPrescriptionService from "../../service/doctor-panel/AppointmentPrescriptionService";
import moment from "moment";
import { FilterMatchMode, FilterOperator } from "primevue/api";

export default {
  data() {
    return {
      isInvestigation: true,
      isPrescription: false,
      isContactExist: "",
      isValid: "",
      isEmailValid: "",
      isPatientExist: "",
      isExist: "",
      collectionList: [],
      bloodGroupList: [],
      genderList: [],
      designationtList: [],
      departmentList: [],
      doctorList: [],
      buildingList: [],
      roomList: [],
      referredByList: [],
      employeeList: [],
      //discountByList: [],
      investigationList: [],
      reportDeliveryTypeList: [],
      productList: [],
      patientInvestigationRequest: {
        patient_id: "",
        aPatient: {
          age_year: "0",
          age_month: "0",
          age_day: "0",
        },
        cdinvestigation_request_date: moment(String(new Date())).format(
          "MM/DD/YYYY"
        ),
        report_delivery_type: "Regular",
        lstInvestigationRequestDetails: [],
        lstInvestigationParticular: [],
        discount_by_id: "Reference",
        discount_percent: 0,
        discount_amount: 0,
        cost_price: 0,
        particular_cost_price: 0,
        total_amount: 0,
      },
      filters: {
        global: { value: null, matchMode: FilterMatchMode.CONTAINS },
        name: { value: null, matchMode: FilterMatchMode.STARTS_WITH },
      },
      initFilters() {
        this.filters = {
          global: { value: null, matchMode: FilterMatchMode.CONTAINS },
          name: {
            operator: FilterOperator.AND,
            constraints: [
              { value: null, matchMode: FilterMatchMode.STARTS_WITH },
            ],
          },
        };
      },
      submitted: false,
      discountByList: [
        { text: "Both", value: "Both" },
        { text: "Hospital", value: "Company" },
        { text: "Reference", value: "Reference" },
      ],
    };
  },
  created() {
    this.patientInvestigationRequestService =
      new PatientInvestigationRequestService();
    this.patientRegistrationService = new PatientRegistrationService();
    this.commonService = new CommonService();
    this.employeeSetupService = new EmployeeSetupService();
    this.doctorSetupService = new DoctorSetupService();
    this.consultancyDepartmentSetupService =
      new ConsultancyDepartmentSetupService();
    this.designationSetupService = new DesignationSetupService();
    this.buildingSetupService = new BuildingSetupService();
    this.roomSetupService = new RoomSetupService();
    this.referredBySetupService = new ReferredBySetupService();
    this.investigationSetupService = new InvestigationSetupService();
    this.productSetupService = new ProductSetupService();
    //this.collectionCenterSetupService = new CollectionCenterSetupService();
    this.patientReportService = new PatientReportService();
    this.validation = new Validation();
    this.appointmentPrescriptionService = new AppointmentPrescriptionService();

    this.commonService
      .GetAllGender()
      .then((data) => (this.genderList = data.data));

    this.commonService
      .GetAllBloodGroup()
      .then((data) => (this.bloodGroupList = data.data));

    this.designationSetupService
      .getAll()
      .then((data) => (this.designationtList = data.data));

    this.consultancyDepartmentSetupService
      .getActiveCombo()
      .then((data) => (this.departmentList = data.data));

    this.buildingSetupService
      .getAll()
      .then((data) => (this.buildingList = data.data));

    this.roomSetupService.getAll().then((data) => (this.roomList = data.data));

    // this.referredBySetupService
    //   .getActiveCombo()
    //   .then((data) => (this.referredByList = data.data));

    // this.referredBySetupService
    //   .getActiveCombo()
    //   .then((data) => this.mergeReferredByInfo(data.data));

    this.referredBySetupService
      .getAll()
      .then((data) => this.mergeReferredByInfo(data.data));

    this.doctorSetupService
      .getAll()
      .then((data) => this.mergeDoctorInfo(data.data));

    this.employeeSetupService
      .getAll()
      .then((data) => this.mergeEmployeeInfo(data.data));

    // this.investigationSetupService
    //   .getAll()
    //   .then((data) => (this.investigationList = data.data));

    this.investigationSetupService
      .getAll()
      .then((data) => this.formatInvestigationList(data.data));

    this.commonService
      .GetAllReportDeliveryType()
      .then((data) => (this.reportDeliveryTypeList = data.data));

    this.productSetupService
      .getAll()
      .then((data) => (this.productList = data.data));
  },
  methods: {
    printInvestigationToken(id) {
      this.patientReportService.GetInvestigationToken(id, this).then((data) => (this.commonService.Downloadfile(data.data)));
    },
    formatInvestigationList(data) {
      let filterData = data.filter(function (el) {
        return el.active == true;
      }, this);
      filterData.forEach((element) => {
        var obj = element;
        obj.investingation_name = element.manual_code + "-" + element.investingation_name;
        this.investigationList.push(obj);
      });
    },
    mergeReferredByInfo(data) {
      let filterData = data.filter(function (el) {
        return el.active == true;
      }, this);
      filterData.forEach((element) => {
        this.referredByList.push({
          value: element.registration_no,
          text: element.manual_code + "-" + element.registration_no + "-" + element.reffered_by_name + "(Referred By)",
        });
      });
    },
    mergeDoctorInfo(data) {
      //this.doctorList = data;
      data.forEach((element) => {
        this.referredByList.push({
          value: element.doc_no,
          text: element.doc_no + "-" + element.doctor_name + "(Doctor)",
        });
      });

      data.forEach((element) => {
        this.doctorList.push({
          department_id: element.department_id,
          doctor_id: element.doctor_id,
          doctor_name: element.doc_no + "-" + element.doctor_name,
        });
      });
    },
    mergeEmployeeInfo(data) {
      let filterData = data.filter(function (el) {
        return el.active == true;
      }, this);
      filterData.forEach((element) => {
        this.referredByList.push({
          value: element.emp_no,
          text: element.emp_no + "-" + element.first_name + "(Employee)",
        });
      });
    },
    // mergeDoctorInfo(data) {
    //   this.doctorList = data;
    //   data.forEach((element) => {
    //     this.discountByList.push({
    //       value: element.doc_no,
    //       text: element.doc_no + "-" + element.doctor_name + "(Doctor)",
    //     });
    //   });
    // },
    // mergeEmployeeInfo(data) {
    //   data.forEach((element) => {
    //     this.discountByList.push({
    //       value: element.emp_no,
    //       text: element.emp_no + "-" + element.first_name + "(Employee)",
    //     });
    //   });
    // },
    invReset() {
      this.isInvestigation = true;
      this.patientInvestigationRequest.ddlinvestigation = "";
      this.isExist = "";
    },
    reset() {
      this.isInvestigation = true;
      this.isPrescription = false;
      this.button = false;
      this.isValid = "";
      this.isEmailValid = "";
      this.isExist = "";
      this.isContactExist = "";
      this.patientInvestigationRequest = {
        patient_id: "",
        aPatient: {
          age_year: "0",
          age_month: "0",
          age_day: "0",
        },
        cdinvestigation_request_date: moment(String(new Date())).format(
          "MM/DD/YYYY"
        ),
        report_delivery_type: "Regular",
        lstInvestigationRequestDetails: [],
        lstInvestigationParticular: [],
        discount_percent: 0,
        discount_amount: 0,
        cost_price: 0,
        particular_cost_price: 0,
        total_amount: 0,
      };
      this.submitted = false;
    },
    doctorReset() {
      this.patientInvestigationRequest.department_id = "";
      this.patientInvestigationRequest.reffered_doctor_id = "";
      this.patientInvestigationRequest.designation_id = "";
      this.patientInvestigationRequest.outside_doctor_name = "";
      this.patientInvestigationRequest.outside_doctor_hospital_name = "";
    },
    filterDoctorsDepartmentWise() {
      return this.doctorList.filter(function (el) {
        return (
          el.department_id === this.patientInvestigationRequest.department_id
        );
      }, this);


    },
    getConsultancyFeeByDoctorId() {
      var aDoctor = this.doctorList.filter(function (el) {
        return el.doctor_id === this.patientInvestigationRequest.doctor_id;
      }, this);

      this.patientInvestigationRequest.consultancy_fee =
        aDoctor[0].consultancy_fee;
      this.patientInvestigationRequest.building_id = aDoctor[0]._building_id;
      this.patientInvestigationRequest.room_id = aDoctor[0].room_id;
    },
    filterRoomsBuildingWise() {
      return this.roomList.filter(function (el) {
        return el.building_id === this.patientInvestigationRequest.building_id;
      }, this);
    },
    filterInvestigation() {
      return this.investigationList.filter(function (el) {
        return el.active == true;
      }, this);
    },
    filterProduct() {
      return this.productList.filter(function (el) {
        return el.product_category_id == 1 && el.active == true;
      }, this);
    },
    getFormatted(data) {
      this.patientInvestigationRequest.aPatient = data;
      if (!this.patientInvestigationRequest.aPatient.patient_id) {
        this.isPatientExist = "Patient not Exist!";
        // this.patientInvestigationRequest = {
        //   patient_id: "",
        //   aPatient: {},
        // };
        this.reset();
      } else {
        this.isPatientExist = "";

        this.patientInvestigationRequest.patient_id =
          this.patientInvestigationRequest.aPatient.patient_id;
        this.patientInvestigationRequest.aPatient.cddob = moment(
          String(this.patientInvestigationRequest.aPatient.dob)
        ).format("YYYY-MM-DD");
        this.patientInvestigationRequest.aPatient.age_year = this.patientInvestigationRequest.aPatient.age_year.toString();
        this.patientInvestigationRequest.aPatient.age_month = this.patientInvestigationRequest.aPatient.age_month.toString();
        this.patientInvestigationRequest.aPatient.age_day = this.patientInvestigationRequest.aPatient.age_day.toString();
      }
    },
    getFormattedPrescriptionWiseInvestigations(data) {
      this.patientInvestigationRequest.aPatient = data[0];
      this.patientInvestigationRequest.patient_id =
        this.patientInvestigationRequest.aPatient.patient_id
      this.patientInvestigationRequest.aPatient.age_year = this.patientInvestigationRequest.aPatient.age_year.toString();
      this.patientInvestigationRequest.aPatient.age_month = this.patientInvestigationRequest.aPatient.age_month.toString();
      this.patientInvestigationRequest.aPatient.age_day = this.patientInvestigationRequest.aPatient.age_day.toString();
      this.patientInvestigationRequest.department_id = data[0].department_id;
      this.patientInvestigationRequest.reffered_doctor_id = data[0].doctor_id;
      this.patientInvestigationRequest.reffered_by_id = data[0].doc_no;
      this.isPrescription = true;

      data.forEach((element) => {
        var aInvestigation =
          this.investigationList.filter(
            function (el) {
              return (
                el.investigation_id ===
                element.investigation_id
              );
            },
            this
          )[0];

        this.patientInvestigationRequest.ddlinvestigation = aInvestigation;
        this.addInvestigation();
      });
    },
    findPatient() {
      let str = this.patientInvestigationRequest.contactOrRegNo;

      if (str.startsWith("PRES")) {
        this.appointmentPrescriptionService
          .prescriptionWiseInvestigations(this.patientInvestigationRequest.contactOrRegNo, this)
          .then((data) => this.getFormattedPrescriptionWiseInvestigations(data.data));
      } else {
        this.isPrescription = false;
        this.patientRegistrationService
          .getByContactOrRegNo(this.patientInvestigationRequest.contactOrRegNo, this)
          .then((data) => this.getFormatted(data.data));
      }
    },
    isExistContact() {
      if (
        this.patientInvestigationRequest.aPatient.contact_no.match(
          /^(01)([0-9]{9})$/
        )
      ) {
        this.patientRegistrationService
          .getByContactOrRegNo(
            this.patientInvestigationRequest.aPatient.contact_no, this
          )
          .then((data) => this.getContact(data.data));
      } else {
        this.isContactExist =
          "Contact number must start with 01 and the required length is 11";
      }
    },
    getContact(data) {
      if (data.contact_no) {
        this.isContactExist = "Duplicate phone number!";
      } else {
        this.isContactExist = "";
      }
    },
    investigationIsExist() {
      var aInvestigation =
        this.patientInvestigationRequest.lstInvestigationRequestDetails.filter(
          function (el) {
            return (
              el.investigation_id ===
              this.patientInvestigationRequest.ddlinvestigation.investigation_id
            );
          },
          this
        );

      if (aInvestigation.length) {
        this.isExist = "This Investigation already added";
        this.isInvestigation = true;
        return;
      } else {
        // var aInv = this.investigationList.filter(function (el) {
        //   return (
        //     el.investigation_id ===
        //     this.patientInvestigationRequest.ddlinvestigation.investigation_id
        //   );
        // }, this);
        // this.collectionCenterSetupService
        //   .getById(aInv[0].collection_center_id)
        //   .then((data) => (this.collectionList = data.data));

        this.isExist = "";
        this.isInvestigation = false;
      }
    },
    addInvestigation() {
      this.submitted = true;
      var aInvestigation =
        this.patientInvestigationRequest.lstInvestigationRequestDetails.filter(
          function (el) {
            return (
              el.investigation_id ===
              this.patientInvestigationRequest.ddlinvestigation.investigation_id
            );
          },
          this
        );

      if (aInvestigation.length) {
        this.isExist = "This Investigation already added";
        //this.isInvestigation = true;
        return;
      } else {
        if (!this.patientInvestigationRequest.ddlinvestigation) {
          return;
        } else if (!this.patientInvestigationRequest.report_delivery_type) {
          return;
        } else if (
          !this.patientInvestigationRequest.cdinvestigation_request_date
        ) {
          return;
        } else {
          this.isExist = "";
          var cost =
            this.patientInvestigationRequest.report_delivery_type == "Regular"
              ? this.patientInvestigationRequest.ddlinvestigation.charge
              : this.patientInvestigationRequest.ddlinvestigation.urgent_carge;

          var deliveryTime = this.patientInvestigationRequest.report_delivery_type == "Regular"?this.expectedHour(this.patientInvestigationRequest.ddlinvestigation.report_delivery_duration) : this.expectedHour(this.patientInvestigationRequest.ddlinvestigation.emmergency_report_delivery_duration);

          var discountAmount =
            (1 *
              cost *
              this.patientInvestigationRequest.ddlinvestigation
                .discount_percent) /
            100;

          var totalAmount = 1 * cost - discountAmount;

          var obj = {
            investigation_id:
              this.patientInvestigationRequest.ddlinvestigation
                .investigation_id,
            _investingation_name:
              this.patientInvestigationRequest.ddlinvestigation
                .investingation_name,
            _investigation_category_head_name:
              this.patientInvestigationRequest.ddlinvestigation
                ._investigation_category_head_name,

            // building_id: this.collectionList._building_id,
            // _building_name: this.collectionList._building_name,
            // room_id: this.collectionList.room_id,
            // _room_name: this.collectionList._room_name,
            building_id: this.patientInvestigationRequest.ddlinvestigation._building_id,
            room_id: this.patientInvestigationRequest.ddlinvestigation._room_id,
            expected_delivery: deliveryTime,

            report_delivery_type:
              this.patientInvestigationRequest.report_delivery_type,

            quantity: 1,
            cost_price: cost,

            discount_percent:
              this.patientInvestigationRequest.ddlinvestigation
                .discount_percent,

            discount_amount: discountAmount,

            total_amount: totalAmount,
          };

          this.patientInvestigationRequest.lstInvestigationRequestDetails.push(
            obj
          );

          this.patientInvestigationRequest.cost_price =
            this.patientInvestigationRequest.cost_price + totalAmount;
          //
          this.calculateTotalAmount();
          // this.patientInvestigationRequest.total_amount =
          //   this.patientInvestigationRequest.cost_price -
          //   this.patientInvestigationRequest.discount_amount;
        }
      }

      this.addParticular(
        this.patientInvestigationRequest.ddlinvestigation.investigation_id
      );
    },
    calculatePerInvestigationDiscountPercent(data) {
      this.patientInvestigationRequest.lstInvestigationRequestDetails.forEach(
        (element) => {
          if (element.investigation_id == data.investigation_id) {
            element.discount_percent = (element.discount_amount / element.cost_price) * 100;
            //var discount_amount = (element.cost_price * element.discount_percent) / 100;
            element.total_amount = element.cost_price - element.discount_amount;
          }
        }
      );
      this.calculateCostPrice();
    },
    calculatePerInvestigationDiscountAmount(data) {
      this.patientInvestigationRequest.lstInvestigationRequestDetails.forEach(
        (element) => {
          if (element.investigation_id == data.investigation_id) {
            element.discount_amount = (element.cost_price * element.discount_percent) / 100;
            element.total_amount = element.cost_price - element.discount_amount;
          }
        }
      );
      this.calculateCostPrice();
    },
    calculateCostPrice() {
      var costPrice = 0;
      this.patientInvestigationRequest.lstInvestigationRequestDetails.forEach(
        (element) => {
          costPrice = costPrice + element.total_amount;
        }
      );
      this.patientInvestigationRequest.cost_price = costPrice;

      this.calculateTotalAmount();
    },
    calculateTotalAmount() {
      this.patientInvestigationRequest.total_amount = Math.ceil(
        this.patientInvestigationRequest.cost_price -
        this.patientInvestigationRequest.discount_amount +
        this.patientInvestigationRequest.particular_cost_price);
    },
    addParticular(id) {
      this.investigationSetupService
        .getById(id)
        .then((data) => this.getParticularFormatted(data));
    },
    addAParticular(data, qty, pname) {
      if (
        this.patientInvestigationRequest.lstInvestigationRequestDetails
          .length <= 0
      ) {
        this.isExist = "Add Investigation";
        return;
      } else {
        this.isExist = "";
        var obj =
          this.patientInvestigationRequest.lstInvestigationParticular.filter(
            function (el) {
              return el.product_id === data.product_id;
            },
            this
          )[0];
        if (obj) {
          qty = parseInt(obj.qty) + qty;

          this.patientInvestigationRequest.lstInvestigationParticular =
            this.patientInvestigationRequest.lstInvestigationParticular.filter(
              function (el) {
                return el.product_id != data.product_id;
              },
              this
            );
        }

        this.patientInvestigationRequest.lstInvestigationParticular.push({
          product_id: data.product_id,
          _product_name: pname,
          uom_id: data.uom_id,
          _uom_name: data._uom_name,
          cost_price: data.cost_price,
          qty: qty,
        });

        this.checkQty();
      }
    },
    getParticularFormatted(data) {
      data.data._lstInvestigationParticular.forEach((element) => {
        // this.patientInvestigationRequest.lstInvestigationParticular.push({
        //   investigation_id: element.investigation_id,
        //   product_id: element.product_id,
        //   _product_name: element._product_name,
        //   uom_id: element.uom_id,
        //   _uom_name: element._uom_name,
        //   cost_price: element.cost_price,
        //   qty: element.qty,
        // });

        this.addAParticular(element, 1, element._product_name);
      });
    },
    checkQty() {
      var totalPrice = 0;
      this.patientInvestigationRequest.lstInvestigationParticular.forEach(
        (element) => {
          if (
            element.qty >
            this.patientInvestigationRequest.lstInvestigationRequestDetails
              .length
          ) {
            element.qty =
              this.patientInvestigationRequest.lstInvestigationRequestDetails.length;
          } else if (element.qty <= 0) {
            element.qty = 1;
          }

          element.totalPrice = element.qty * element.cost_price;
          totalPrice += element.totalPrice;
        }
      );
      this.patientInvestigationRequest.particular_cost_price = totalPrice;
      this.calculateTotalAmount();
    },
    getDiscountPercent() {
      this.patientInvestigationRequest.discount_percent = Math.ceil(
        (this.patientInvestigationRequest.discount_amount /
          this.patientInvestigationRequest.cost_price) *
        100);

      // this.patientInvestigationRequest.total_amount = Math.ceil(
      //   this.patientInvestigationRequest.cost_price -
      //   this.patientInvestigationRequest.discount_amount);
      this.calculateTotalAmount();
    },
    getDiscountAmount() {
      this.patientInvestigationRequest.discount_amount =
        (this.patientInvestigationRequest.cost_price *
          this.patientInvestigationRequest.discount_percent) /
        100;

      // this.patientInvestigationRequest.total_amount = Math.ceil(
      //   this.patientInvestigationRequest.cost_price -
      //   this.patientInvestigationRequest.discount_amount);
      this.calculateTotalAmount();
    },
    expectedHour(h) {
      // var today = new Date();
      // today.setHours(today.getHours() + h);
      var travelTime = moment().add(h, 'hours').format('YYYY-MM-DD hh:mm A');
      return travelTime;
    },
    deleteFromList(data) {
      this.patientInvestigationRequest.lstInvestigationRequestDetails =
        this.patientInvestigationRequest.lstInvestigationRequestDetails.filter(
          function (el) {
            return el.investigation_id != data.investigation_id;
          },
          this
        );
      this.patientInvestigationRequest.cost_price =
        this.patientInvestigationRequest.cost_price - data.total_amount;
      this.calculateTotalAmount();
      this.checkQty();

      if (
        !this.patientInvestigationRequest.lstInvestigationRequestDetails.length
      ) {
        this.patientInvestigationRequest.lstInvestigationParticular = [];
        this.patientInvestigationRequest.particular_cost_price = 0;
        this.calculateTotalAmount();
      }
    },
    deleteFromParticularList(data) {
      this.patientInvestigationRequest.lstInvestigationParticular =
        this.patientInvestigationRequest.lstInvestigationParticular.filter(
          function (el) {
            return el.product_id != data.product_id;
          },
          this
        );
      this.patientInvestigationRequest.particular_cost_price =
        this.patientInvestigationRequest.particular_cost_price -
        data.totalPrice;
      this.calculateTotalAmount();
    },
    async add() {
      try {
        if (!this.patientInvestigationRequest.aPatient.first_name) {
          return;
        } else if (!this.patientInvestigationRequest.aPatient.age_year) {
          return;
        } else if (!this.patientInvestigationRequest.aPatient.age_month) {
          return;
        } else if (!this.patientInvestigationRequest.aPatient.age_day) {
          return;
        } else if (!this.patientInvestigationRequest.aPatient.contact_no) {
          return;
        } else if (
          !this.patientInvestigationRequest.cdinvestigation_request_date
        ) {
          return;
        } else if (this.isContactExist) {
          return;
        } else if (
          !this.patientInvestigationRequest.lstInvestigationRequestDetails
            .length
        ) {
          this.isExist = "Please add a Investigation";
          return;
        } else {
          this.patientInvestigationRequest.investigation_request_date = moment(
            String(
              this.patientInvestigationRequest.cdinvestigation_request_date
            )
          ).format("YYYY-MM-DD");

          var currentDate = new Date();
          var dob = new Date(currentDate.getFullYear() - this.patientInvestigationRequest.aPatient.age_year, currentDate.getMonth() - this.patientInvestigationRequest.aPatient.age_month, currentDate.getDate() - this.patientInvestigationRequest.aPatient.age_day);
          this.patientInvestigationRequest.aPatient.dob = moment(
            String(dob)
          ).format("YYYY-MM-DD");
          //
          this.patientInvestigationRequest.aPatient.age_year = this.patientInvestigationRequest.aPatient.age_year.toString();
          this.patientInvestigationRequest.aPatient.age_month = this.patientInvestigationRequest.aPatient.age_month.toString();
          this.patientInvestigationRequest.aPatient.age_day = this.patientInvestigationRequest.aPatient.age_day.toString();

          let result = await this.patientInvestigationRequestService.add(
            this.patientInvestigationRequest, this
          );
          if (result.status === 200) {
            this.$toast.add({
              severity: "success",
              summary: "Success",
              detail: "Saved Successfully",
              life: 10000,
            });
            //this.reset();
            //this.printInvestigationToken(result.data);
            this.confirmPay(result.data);
          } else if (result.status === 401) {
            this.$toast.add({
              severity: "error",
              summary: "Error",
              detail: result.data,
              life: 10000,
            });
          } else {
            this.$toast.add({
              severity: "error",
              summary: "Failed",
              detail: result.res,
              life: 10000,
            });
          }
        }
      } catch (error) {
        this.$toast.add({
          severity: "error",
          summary: "Failed",
          detail: error,
          life: 10000,
        });
      }
    },
    confirmPay(data) {
      this.$confirm.require({
        message: 'Are you sure you want to pay now?',
        header: 'Confirmation',
        icon: 'pi pi-exclamation-triangle',
        accept: () => {
          localStorage.setItem('inv_request_id', data);
          this.$router.push("/investigation-payment-collection");
        },
        reject: () => {
          this.printInvestigationToken(data);
          this.reset();
        },

      });
    }
  },
};
