import moment from "moment";
import Validation from "../../scripts/validation";
import { FilterService, FilterMatchMode, FilterOperator } from "primevue/api";

// Prescription
import AppointmentPrescriptionService from "../../service/doctor-panel/AppointmentPrescriptionService";
import AdmissionPrescriptionService from "../../service/prescription/AdmissionPrescriptionService";
// Prescription Treatment
import MedicineSetupService from "../../service/setup/MedicineSetupService";
import TreatmentRulesSetupService from "../../service/setup/TreatmentRulesSetupService";
import TreatmentDurationSetupService from "../../service/setup/TreatmentDurationSetupService";
import TreatmentDoseSetupService from "../../service/setup/TreatmentDoseSetupService";
// History
import PatientProfileService from "../../service/profiles/PatientProfileService";
import ComplaintSetupService from "../../service/setup/ComplaintSetupService";
import FoodService from "../../service/setup/FoodService";
import OnExaminationSetupService from "../../service/setup/OnExaminationSetupService";
import DietChartTemplateSetupService from "../../service/setup/DietChartTemplateSetupService";
import DietChartSetupService from "../../service/setup/DietChartSetupService";
import DiagnosisSetupService from "../../service/setup/DiagnosisSetupService";
import CommonService from "../../service/CommonService";
// Advice
import AdviceSetupService from "../../service/setup/AdviceSetupService";

import GlobalSettingsService from "../../service/security/GlobalSettingsService";

export default {
  data() {
    return {
      filteredItems: null,
      submitted: false,
      filters: {
        global: { value: null, matchMode: FilterMatchMode.CONTAINS },
        name: { value: null, matchMode: FilterMatchMode.STARTS_WITH },
      },
      initFilters() {
        this.filters = {
          global: { value: null, matchMode: FilterMatchMode.CONTAINS },
          name: {
            operator: FilterOperator.AND,
            constraints: [
              { value: null, matchMode: FilterMatchMode.STARTS_WITH },
            ],
          },
        };
      },

      // Prescription
      historyTypeList: [
        { label: "Medicine", value: "Medicine History" },
        { label: "Medical", value: "Medical History" },
      ],
      admittedPatientInfo: {},
      prescriptionDataList: [],
      tempData: [],
      headerName: "Sections",
      admissionPrescriptionInfo: {
        allAdvice: false,
        lstAdmissionPrescriptionInvestigation: [],
        lstAdmissionPrescriptionTreatment: [],
        lstAdmissionPrescriptionAdvice: [],
      },

      // Prescription Treatment
      treatmentAddDialog: false,
      treatmentDialog: false,
      dose: {},
      treatmentDoseSetup: {},
      rule: {},
      treatmentRuleSetup: {},
      duration: {},
      treatmentDurationSetup: {},
      treatmentSetup: {
        type: 'Medicine History'
      },
      medicineList: [],
      treatmentRulesList: [],
      treatmentDurationList: [],
      treatmentDoseList: [],

      // Section
      SectionDialog: false,

      // History
      complaintSetup: {},
      chiefComplainDialog: false,
      compliantList: [],
      chiefComplain: {},
      chiefComplainList: [],

      foodSetup: {},
      allergyDetailsDialog: false,
      allergyDetails: {},
      foodList: [],
      allergyTypeList: [],

      onExaminationDialog: false,
      otherOnExaminations: {},
      patientOnExaminationList: [],
      onExaminationList: [],

      iOChartDialog: false,
      iOChart: {},
      iOChartList: [],

      diabetesChartDialog: false,
      diabetesChart: {
        aAdmissionServiceDetails: {}
      },
      diabetesChartList: [],
      diabetesMealList: [],

      dietChartDialog: false,
      dietChart: {},
      dietChartList: [],
      dietChartTemplateList: [],
      dietChartTempList: [],

      currentMedicationsDialog: false,
      currentMedications: {},
      currentMedicationsList: [],

      diagnosisSetup: {},
      pastMedicalHistoryDialog: false,
      pastMedicalHistory: {},
      pastMedicalHistoryList: [],
      diagnosisList: [],

      patientLatestHistory: {
        lstPatientOnExamination: [],
        lstPatientAllergyDetails: [],
        lstPatientDiabetesChart: [],
        lstPatientDietChart: [],
      },

      department_id: "",
      admission_id: "",
      newAdviceDialog: false,
      adviceSetup: {},

      globalSetting: {},
    };
  },
  created() {
    // past date-year disabled
    let today = new Date();
    let month = today.getMonth();
    let year = today.getFullYear();
    let prevMonth = (month === 0) ? 11 : month - 0;
    let prevYear = (prevMonth === 11) ? year - 0 : year;
    this.minDate = new Date();
    this.minDate.setMonth(prevMonth);
    this.minDate.setFullYear(prevYear);

    // Prescription
    this.admissionPrescriptionService = new AdmissionPrescriptionService();
    this.appointmentPrescriptionService = new AppointmentPrescriptionService();

    // Prescription Treatment
    this.medicineSetupService = new MedicineSetupService();
    this.treatmentRulesSetupService = new TreatmentRulesSetupService();
    this.treatmentDurationSetupService = new TreatmentDurationSetupService();
    this.treatmentDoseSetupService = new TreatmentDoseSetupService();

    // History
    this.patientProfileService = new PatientProfileService();

    this.complaintSetupService = new ComplaintSetupService();
    this.foodService = new FoodService();
    this.onExaminationSetupService = new OnExaminationSetupService();
    this.dietChartTemplateSetupService = new DietChartTemplateSetupService();
    this.dietChartSetupService = new DietChartSetupService();

    this.diagnosisSetupService = new DiagnosisSetupService();
    this.commonService = new CommonService();
    this.validation = new Validation();

    // Advice
    this.adviceSetupService = new AdviceSetupService();

    this.globalSettingsService = new GlobalSettingsService();

    // shortcut key start
    let that = this;
    document.onkeyup = function (e) {
      if (e.ctrlKey && e.altKey && e.which == 73) {
        that.getInvestigation();
      } else if (e.ctrlKey && e.altKey && e.which == 84) {
        that.getTreatment();
      } else if (e.ctrlKey && e.altKey && e.which == 65) {
        that.getAdvice();
      }
      // else if (e.ctrlKey && e.altKey && e.shiftKey) {

      // }
    };
    // shortcut key end

    this.userInfo = JSON.parse(localStorage.getItem("userInfo"));

    if (this.userInfo.doctor_id) {
      this.getPrescriptionData();

      this.doctor_id = this.userInfo.doctor_id;
      this.admissionPrescriptionInfo.doctor_id = this.doctor_id;
    }

    this.globalSettingsService
      .getBySettingName("Previous Prescription History Load on Current Prescription")
      .then((data) => (this.globalSetting = data.data));
  },
  methods: {
    getPrescriptionData() {
      //this.userInfo = JSON.parse(localStorage.getItem("userInfo"));
      this.appointmentPrescriptionService
        .getPrescriptionDataByDoctorId(this.userInfo.doctor_id)
        .then((data) => (this.prescriptionDataList = data.data));
    },

    hideDialog() {
      var patientId = JSON.parse(localStorage.getItem("patientIdForTreatment")).toString();

      // Chief Complain
      this.chiefComplainDialog = false;
      this.chiefComplain = {
        patient_id: patientId
      };

      // On Examination
      this.onExaminationDialog = false;
      this.otherOnExaminations = {
        patient_id: patientId
      };

      this.SectionDialog = false;
      this.treatmentAddDialog = false;
      this.treatmentSetup = {
        type: 'Medicine History'
      };
      this.treatmentDialog = false;

      this.foodSetup = {};
      this.allergyDetailsDialog = false;
      this.allergyDetails = {
        patient_id: patientId
      };

      this.iOChartDialog = false;
      this.iOChart = {
        patient_id: patientId
      };

      this.diabetesChartDialog = false;
      this.diabetesChart = {
        aAdmissionServiceDetails: {},
        patient_id: patientId
      };

      this.dietChartDialog = false;
      this.dietChart = {
        patient_id: patientId
      };

      this.newAdviceDialog = false;
      this.adviceSetup = {};

      this.diagnosisSetup = {};
      this.pastMedicalHistoryDialog = false;
      this.pastMedicalHistory = {};

      this.currentMedicationsDialog = false;
      this.currentMedications = {};

      this.submitted = false;
    },
    // Get Previous Chief Complain
    getComplainsByPatientId(patient_id) {
      this.chiefComplain.patient_id = patient_id;
      this.patientProfileService.getComplainsByPatientId(patient_id).then((data) => (this.chiefComplainList = data.data));
    },
    // Get, Add Complain
    getComplain() {
      this.complaintSetupService
        .getActiveCombo()
        .then((data) => (this.compliantList = data.data));
    },
    async addComplain() {
      this.submitted = true;
      try {
        if (!this.complaintSetup.compilants_name) {
          return;
        } else {
          //this.complaintSetup.compliants_group_id = "1"
          //this.complaintSetup.is_favourite = true;
          let result = await this.complaintSetupService.add(this.complaintSetup);
          if (result.status === 200) {
            this.getComplain();
            return result.data;
          } else {
            this.$toast.add({
              severity: "error",
              summary: "Failed",
              detail: result.res,
              life: 10000,
            });
          }
        }
      } catch (error) {
        this.$toast.add({
          severity: "error",
          summary: "Failed",
          detail: error,
          life: 10000,
        });
      }
    },
    // Add Chief Complain
    searchComplain(event) {
      setTimeout(() => {
        if (!event.query.trim().length) {
          this.filteredItems = [...this.compliantList];
        }
        else {
          //start with
          this.filteredItems = this.compliantList.filter((item) => {
            return item.text.toLowerCase().startsWith(event.query.toLowerCase());
          });
        }
      }, 250);
    },
    newChiefComplain() {
      this.chiefComplainDialog = true;
    },
    async addChiefComplain() {
      this.submitted = true;
      this.chiefComplain.patient_id = JSON.parse(localStorage.getItem("patientIdForTreatment")).toString();
      if (!this.chiefComplain.ddlcomplain) {
        return;
      } else if (!this.chiefComplain.patient_id) {
        return;
      } else {
        if (!this.chiefComplain.cdfrom_date) {
          this.chiefComplain.cdfrom_date = moment(String(new Date())).format("MM/DD/YYYY");
        }
        this.chiefComplain.from_date = moment(
          String(this.chiefComplain.cdfrom_date)
        ).format("YYYY-MM-DD");
        // complain is exist check start
        if (!this.chiefComplain.ddlcomplain.value) {
          var isComplainExist = this.compliantList.filter(function (el) {
            return el.text == this.chiefComplain.ddlcomplain;
          }, this);

          if (isComplainExist.length == 0) {
            this.complaintSetup.compilants_name = this.chiefComplain.ddlcomplain;
            var complainId = await this.addComplain();
            this.chiefComplain.ddlcomplain = {
              text: "",
              value: "",
            };
            this.chiefComplain.ddlcomplain.value = complainId.toString();
          } else {
            this.chiefComplain.ddlcomplain = {
              text: "",
              value: "",
            };
            this.chiefComplain.ddlcomplain.value = isComplainExist[0].value;
          }
        }
        // complain is exist check end

        var isDuplicate = this.chiefComplainList.filter(function (el) {
          return el.compilants_id == this.chiefComplain.ddlcomplain.value;
        }, this);
        this.chiefComplain.compilants_id = this.chiefComplain.ddlcomplain.value;
        if (isDuplicate.length == 0) {
          let result = await this.patientProfileService.complainSave(this.chiefComplain);
          if (result.status === 201) {
            this.$toast.add({
              severity: "success",
              summary: "Success",
              detail: "Saved Successfully",
              life: 10000,
            });
            this.getComplainsByPatientId(this.chiefComplain.patient_id);
            this.hideDialog();
          } else if (result.status === 401) {
            this.$toast.add({
              severity: "error",
              summary: "Error",
              detail: result.data,
              life: 10000,
            });
          } else {
            this.$toast.add({
              severity: "error",
              summary: "Failed",
              detail: result.res,
              life: 10000,
            });
          }
        } else {
          this.chiefComplain.patient_complain_id = isDuplicate[0].patient_complain_id;
          let result = await this.patientProfileService.complainUpdate(this.chiefComplain);
          if (result.status === 202) {
            this.getComplainsByPatientId(this.chiefComplain.patient_id);
            this.hideDialog();
            this.$toast.add({
              severity: "success",
              summary: "Success",
              detail: "Updated Successfully",
              life: 10000,
            });
          } else if (result.status === 401) {
            this.$toast.add({
              severity: "error",
              summary: "Error",
              detail: result.data,
              life: 10000,
            });
          } else {
            this.$toast.add({
              severity: "error",
              summary: "Failed",
              detail: result.res,
              life: 10000,
            });
          }
        }
      }


      // var patientId = JSON.parse(localStorage.getItem("patientIdForTreatment")).toString();
      // this.chiefComplain.patient_id = patientId;
      // this.submitted = true;
      // if (!this.chiefComplain.compilants_id) {
      //   return;
      // } else if (!this.chiefComplain.patient_id) {
      //   return;
      // } else if (!this.chiefComplain.cdfrom_date) {
      //   return;
      // } else if (this.validation.isIdExists(this.chiefComplainList, 'compilants_id', this.chiefComplain.compilants_id, 'patient_complain_id', this.chiefComplain.patient_complain_id, this)) {
      //   return;
      // } else {
      //   this.chiefComplain.from_date = moment(
      //     String(this.chiefComplain.cdfrom_date)
      //   ).format("YYYY-MM-DD");
      //   if (!this.chiefComplain.patient_complain_id) {
      //     let result = await this.patientProfileService.complainSave(this.chiefComplain);
      //     if (result.status === 201) {
      //       this.$toast.add({
      //         severity: "success",
      //         summary: "Success",
      //         detail: "Saved Successfully",
      //         life: 10000,
      //       });
      //       this.getComplainsByPatientId(this.chiefComplain.patient_id);
      //       this.hideDialog();
      //     } else if (result.status === 401) {
      //       this.$toast.add({
      //         severity: "error",
      //         summary: "Error",
      //         detail: result.data,
      //         life: 10000,
      //       });
      //     } else {
      //       this.$toast.add({
      //         severity: "error",
      //         summary: "Failed",
      //         detail: result.res,
      //         life: 10000,
      //       });
      //     }
      //   } else {
      //     let result = await this.patientProfileService.complainUpdate(this.chiefComplain);
      //     if (result.status === 202) {
      //       this.getComplainsByPatientId(this.chiefComplain.patient_id);
      //       this.hideDialog();
      //       this.$toast.add({
      //         severity: "success",
      //         summary: "Success",
      //         detail: "Updated Successfully",
      //         life: 10000,
      //       });
      //     } else if (result.status === 401) {
      //       this.$toast.add({
      //         severity: "error",
      //         summary: "Error",
      //         detail: result.data,
      //         life: 10000,
      //       });
      //     } else {
      //       this.$toast.add({
      //         severity: "error",
      //         summary: "Failed",
      //         detail: result.res,
      //         life: 10000,
      //       });
      //     }
      //   }
      // }
    },

    // Delete Chief Complain
    async deleteComplain(data) {
      let result = await this.patientProfileService.complainDelete(data);
      if (result.status === 202) {
        this.getComplainsByPatientId(this.chiefComplain.patient_id);
      } else {
        this.$toast.add({
          severity: "error",
          summary: "Failed",
          detail: result.res,
          life: 10000,
        });
      }
    },
    // Get Previous lstPatientOnExamination, lstPatientAllergyDetails, lstPatientDiabetesChart, lstPatientDietChart,
    getLatestHistoryByPatientIdForPrescription() {
      var patientId = JSON.parse(localStorage.getItem("patientIdForTreatment")).toString();
      this.patientProfileService.getLatestHistoryByPatientIdForPrescription(patientId).then((data) => (this.patientLatestHistory = data.data));
    },

    // Add On Examination
    newOnExamination() {
      this.getOnExaminationsPopup();
      this.onExaminationDialog = true;
    },
    getOnExaminationsPopup() {
      var patientId = JSON.parse(localStorage.getItem("patientIdForTreatment"));
      this.otherOnExaminations.patient_id = patientId.toString();

      this.popupOnExaminationsData = [];
      this.onExaminationList.forEach((element) => {
        this.popupOnExaminationsData.push({
          _on_examination_name: element.text,
          on_examination_id: element.value,
          on_examination_result: String(0),
          patient_id: patientId.toString()
        });
      });

      this.onExaminationPopupDialog = true;
    },
    async addOnExamination() {
      this.submitted = true;
      this.requred = "";
      this.popupOnExaminationsData.forEach((element) => {
        if (!element.on_examination_result) {
          this.requred = "required";
        }
      });

      if (this.requred) {
        this.$toast.add({
          severity: "error",
          summary: "Required",
          detail: "Please fill in the required fields",
          life: 10000,
        });
        return;
      }
      if (this.requred) {
        return;
      } else {
        var onExaminationsList = this.popupOnExaminationsData.filter(function (elm) {
          return elm.on_examination_result != "0";
        }, this);
        let result = 0;
        if(this.otherOnExaminations.description){
          result = await this.patientProfileService.otherOnExaminationSave(this.otherOnExaminations);
        }
        if(onExaminationsList.length > 0){
          result = await this.patientProfileService.onExaminationSave(onExaminationsList);
        }
        
        if (result.status === 201) {
          this.getLatestHistoryByPatientIdForPrescription();
          this.hideDialog();
          this.$toast.add({
            severity: "success",
            summary: "Success",
            detail: "Saved Successfully",
            life: 10000,
          });
        } else {
          this.$toast.add({
            severity: "error",
            summary: "Failed",
            detail: result.res,
            life: 10000,
          });
        }
      }
    },

    // Get, Add Food
    getFood() {
      this.foodService
        .getActiveCombo()
        .then((data) => (this.foodList = data.data));
    },
    async addFood() {
      try {
        if (!this.foodSetup.food_name) {
          return;
        } else {
          this.foodSetup.active = true;
          let result = await this.foodService.add(this.foodSetup);
          if (result.status === 200) {
            this.getFood();
            return result.data;
          } else {
            this.$toast.add({
              severity: "error",
              summary: "Failed",
              detail: result.res,
              life: 10000,
            });
          }
        }
      } catch (error) {
        this.$toast.add({
          severity: "error",
          summary: "Failed",
          detail: error,
          life: 10000,
        });
      }
    },

    // Add Allergy Details
    searchFood(event) {
      setTimeout(() => {
        if (!event.query.trim().length) {
          this.filteredItems = [...this.foodList];
        }
        else {
          //start with
          this.filteredItems = this.foodList.filter((item) => {
            return item.text.toLowerCase().startsWith(event.query.toLowerCase());
          });
        }
      }, 250);
    },
    newAllergyDetails() {
      this.allergyDetailsDialog = true;
    },
    async addAllergyDetails() {
      this.submitted = true;
      this.allergyDetails.patient_id = JSON.parse(localStorage.getItem("patientIdForTreatment")).toString();
      if (!this.allergyDetails.ddlfood) {
        return;
      } else if (!this.allergyDetails.patient_id) {
        return;
      } else if (!this.allergyDetails.type) {
        return;
      } else if (!this.allergyDetails.cdfrom_date) {
        return;
      } else {
        this.allergyDetails.from_date = moment(
          String(this.allergyDetails.cdfrom_date)
        ).format("YYYY-MM-DD");
        // food is exist check start
        if (!this.allergyDetails.ddlfood.value) {
          var isFoodExist = this.foodList.filter(function (el) {
            return el.text == this.allergyDetails.ddlfood;
          }, this);

          if (isFoodExist.length == 0) {
            this.foodSetup.food_name = this.allergyDetails.ddlfood;
            var foodId = await this.addFood();
            this.allergyDetails.ddlfood = {
              text: "",
              value: "",
            };
            this.allergyDetails.ddlfood.value = foodId.toString();
          } else {
            this.allergyDetails.ddlfood = {
              text: "",
              value: "",
            };
            this.allergyDetails.ddlfood.value = isFoodExist[0].value;
          }
        }
        // food is exist check end

        var isDuplicate = this.patientLatestHistory.lstPatientAllergyDetails.filter(function (el) {
          return el.food_id == this.allergyDetails.ddlfood.value;
        }, this);
        this.allergyDetails.food_id = this.allergyDetails.ddlfood.value;
        if (isDuplicate.length == 0) {
          let result = await this.patientProfileService.allergyDetailsSave(this.allergyDetails);
          if (result.status === 201) {
            this.$toast.add({
              severity: "success",
              summary: "Success",
              detail: "Saved Successfully",
              life: 10000,
            });
            this.getLatestHistoryByPatientIdForPrescription();
            this.hideDialog();
          } else if (result.status === 401) {
            this.$toast.add({
              severity: "error",
              summary: "Error",
              detail: result.data,
              life: 10000,
            });
          } else {
            this.$toast.add({
              severity: "error",
              summary: "Failed",
              detail: result.res,
              life: 10000,
            });
          }
        } else {
          this.allergyDetails.patient_allergy_details_id = isDuplicate[0].patient_allergy_details_id;
          let result = await this.patientProfileService.allergyDetailsUpdate(this.allergyDetails);
          if (result.status === 202) {
            this.getLatestHistoryByPatientIdForPrescription();
            this.hideDialog();
            this.$toast.add({
              severity: "success",
              summary: "Success",
              detail: "Updated Successfully",
              life: 10000,
            });
          } else if (result.status === 401) {
            this.$toast.add({
              severity: "error",
              summary: "Error",
              detail: result.data,
              life: 10000,
            });
          } else {
            this.$toast.add({
              severity: "error",
              summary: "Failed",
              detail: result.res,
              life: 10000,
            });
          }
        }
      }
    },

    // Delete Allergy Details
    async deleteAllergyDetails(data) {
      let result = await this.patientProfileService.allergyDetailsDelete(data);
      if (result.status === 202) {
        this.getLatestHistoryByPatientIdForPrescription();
      } else {
        this.$toast.add({
          severity: "error",
          summary: "Failed",
          detail: result.res,
          life: 10000,
        });
      }
    },

    // Add IO Chart
    newIOChart() {
      this.iOChartDialog = true;
    },
    async addIOChart() {
      this.submitted = true;
      if (!this.iOChart.patient_id) {
        return;
      } else {
        let result = await this.patientProfileService.iOChartSave(this.iOChart);
        if (result.status === 201) {
          var patientId = JSON.parse(localStorage.getItem("patientIdForTreatment"));
          this.getIOChartByPatientId(patientId);
          this.hideDialog();
          this.$toast.add({
            severity: "success",
            summary: "Success",
            detail: "Saved Successfully",
            life: 10000,
          });
        } else {
          this.$toast.add({
            severity: "error",
            summary: "Failed",
            detail: result.res,
            life: 10000,
          });
        }
      }
    },

    // Add Diabetes Chart
    newDiabetesChart() {
      this.diabetesChartDialog = true;
    },
    async addDiabetesChart() {
      this.submitted = true;
      if (!this.diabetesChart.meal) {
        return;
      } else if (!this.diabetesChart.before) {
        return;
      } else if (!this.diabetesChart.after) {
        return;
      } else {
        //var admission_id = JSON.parse(localStorage.getItem("admissionIdForDiabetesService"));
        //this.diabetesChart.aAdmissionServiceDetails.admission_id = String(admission_id);
        var patientId = JSON.parse(localStorage.getItem("patientIdForTreatment"));
        this.diabetesChart.patient_id = String(patientId);
        let result = await this.patientProfileService.diabetesChartSave(this.diabetesChart);
        if (result.status === 201) {
          this.getLatestHistoryByPatientIdForPrescription();
          this.hideDialog();
          this.$toast.add({
            severity: "success",
            summary: "Success",
            detail: "Saved Successfully",
            life: 10000,
          });
        } else {
          this.$toast.add({
            severity: "error",
            summary: "Failed",
            detail: result.res,
            life: 10000,
          });
        }
      }
