        //   String(this.pastMedicalHistory.cdfrom_date)
        // ).format("YYYY-MM-DD");
        // diagnosis is exist check start
        if (!this.pastMedicalHistory.ddldiagnosis.diagnosis_id) {
          var isDiagnosisExist = this.diagnosisList.filter(function (el) {
            return el.diagnosis_name == this.pastMedicalHistory.ddldiagnosis;
          }, this);

          if (isDiagnosisExist.length == 0) {
            this.diagnosisSetup.diagnosis_name = this.pastMedicalHistory.ddldiagnosis;
            var diagnosisId = await this.addDiagnosis();
            this.pastMedicalHistory.ddldiagnosis = {
              diagnosis_name: "",
              diagnosis_id: "",
            };
            this.pastMedicalHistory.ddldiagnosis.diagnosis_id = diagnosisId.toString();
          } else {
            this.pastMedicalHistory.ddldiagnosis = {
              diagnosis_name: "",
              diagnosis_id: "",
            };
            this.pastMedicalHistory.ddldiagnosis.diagnosis_id = isDiagnosisExist[0].diagnosis_id;
          }
        }
        // diagnosis is exist check end

        var isDuplicate = this.pastMedicalHistoryList.filter(function (el) {
          return el.diagnosis_id == this.pastMedicalHistory.ddldiagnosis.diagnosis_id;
        }, this);
        this.pastMedicalHistory.diagnosis_id = this.pastMedicalHistory.ddldiagnosis.diagnosis_id;
        if (isDuplicate.length == 0) {
          let result = await this.patientProfileService.pastHistorySave(this.pastMedicalHistory);
          if (result.status === 201) {
            this.$toast.add({
              severity: "success",
              summary: "Success",
              detail: "Saved Successfully",
              life: 10000,
            });
            this.getPastHistoryByPatientId(this.pastMedicalHistory.patient_id);
            this.hideDialog();
          } else if (result.status === 401) {
            this.$toast.add({
              severity: "error",
              summary: "Error",
              detail: result.data,
              life: 10000,
            });
          } else {
            this.$toast.add({
              severity: "error",
              summary: "Failed",
              detail: result.res,
              life: 10000,
            });
          }
        } else {
          this.hideDialog();
        }
      }
    },
    confirmDeletePastMedicalHistory(data) {
      this.$confirm.require({
        message: 'Do you want to delete this record?',
        header: 'Delete Confirmation',
        icon: 'pi pi-info-circle',
        acceptClass: 'p-button-danger',
        accept: () => {
          this.deletePastMedicalHistory(data);
        },
        reject: () => {
          return;
        }
      });
    },
    async deletePastMedicalHistory(data) {
      let result = await this.patientProfileService.pastHistoryDelete(data);
      if (result.status === 202) {
        this.getPastHistoryByPatientId(data.patient_id)
      } else {
        this.$toast.add({
          severity: "error",
          summary: "Failed",
          detail: result.res,
          life: 10000,
        });
      }
    },

    // Medicine History
    // Get Previous Medicine History
    getMedicineHistoryByPatientId(patient_id) {
      this.currentMedications.patient_id = patient_id;
      this.patientProfileService.getCurrentMedicationsByPatientId(patient_id).then((data) => (this.currentMedicationsList = data.data));
    },
    // Add Medicine History
    newMedicineHistory() {
      this.currentMedicationsDialog = true;
    },
    async addCurrentMedications() {
      this.submitted = true;
      if (this.treatmentSetup.type == 'Medicine History') {

        if(this.treatmentSetup.ddltreatment_dose){
          await this.addDose();
          this.treatmentSetup.treatment_dose_id = this.dose.value;
        }
        if(this.treatmentSetup.ddltreatment_rules){
          await this.addRule();
          this.treatmentSetup.treatment_rules_id = this.rule.value;
        }
        if(this.treatmentSetup.ddltreatment_duration){
          await this.addDuration();
          this.treatmentSetup.treatment_duration_id = this.duration.value;
        }

        this.treatmentSetup.medicine_id = this.treatmentSetup.ddlmedicine.value;
      }
      this.treatmentSetup.patient_id = JSON.parse(localStorage.getItem("patientIdForTreatment")).toString();
      if (!this.treatmentSetup.ddlmedicine && this.treatmentSetup.type == 'Medicine History') {
        return;
      } else if (!this.treatmentSetup.description && this.treatmentSetup.type == 'Medical History') {
        return;
      } else if (!this.treatmentSetup.patient_id) {
        return;
      } else if (this.treatmentSetup.type == 'Medicine History' && this.validation.isIdExists(this.currentMedicationsList, 'medicine_id', this.treatmentSetup.medicine_id, 'patient_current_medications_id', this.treatmentSetup.patient_current_medications_id, this)) {
        return;
      } else {
        let result = await this.patientProfileService.currentMedicationsSave(this.treatmentSetup);
        if (result.status === 201) {
          this.getMedicineHistoryByPatientId(this.treatmentSetup.patient_id);
          this.hideDialog();
          this.$toast.add({
            severity: "success",
            summary: "Success",
            detail: "Saved Successfully",
            life: 10000,
          });
        } else {
          this.$toast.add({
            severity: "error",
            summary: "Failed",
            detail: result.res,
            life: 10000,
          });
        }
      }
    },
    confirmDeleteCurrentMedications(data) {
      this.$confirm.require({
        message: 'Do you want to delete this record?',
        header: 'Delete Confirmation',
        icon: 'pi pi-info-circle',
        acceptClass: 'p-button-danger',
        accept: () => {
          this.deleteCurrentMedications(data);
        },
        reject: () => {
          return;
        }
      });
    },
    async deleteCurrentMedications(data) {
      let result = await this.patientProfileService.currentMedicationsDelete(data);
      if (result.status === 202) {
        this.getMedicineHistoryByPatientId(data.patient_id)
      } else {
        this.$toast.add({
          severity: "error",
          summary: "Failed",
          detail: result.res,
          life: 10000,
        });
      }
    },
  },
};
