import PatientPaymentService from "../../service/cash-counter/PatientPaymentService";
import PatientInvestigationRequestService from "../../service/front-desk/PatientInvestigationRequestService";
import PaymentTypeSetupService from "../../service/setup/PaymentTypeSetupService";
import PatientReportService from "../../service/report/PatientReportService";
import CommonService from "../../service/CommonService";
import moment from "moment";
import { FilterMatchMode, FilterOperator } from "primevue/api";

export default {
  data() {
    return {
      isPayment: true,
      addList: false,
      button: false,
      isCash: false,
      //isPaid: "",
      isCashPaid: "",
      //isDueExist: "",
      //isExist: "",
      paymentTypeList: [],
      totalPtyAmount: 0,
      patientPayment: {
        cdpayment_date: moment(String(new Date())).format(
          "MM/DD/YYYY"
        ),
        ptypaid_amount: 0,
        total_amount_inf: 0,
        paymentTypeCash: "1",
        invoice_id: "",
        aPatient: {},
        lstPatientPaymentDetails: [],
        discount_amount: 0,
        discount_percent: 0,
      },
      filters: {
        global: { value: null, matchMode: FilterMatchMode.CONTAINS },
        name: { value: null, matchMode: FilterMatchMode.STARTS_WITH },
      },
      initFilters() {
        this.filters = {
          global: { value: null, matchMode: FilterMatchMode.CONTAINS },
          name: {
            operator: FilterOperator.AND,
            constraints: [
              { value: null, matchMode: FilterMatchMode.STARTS_WITH },
            ],
          },
        };
      },
      submitted: false,
    };
  },
  created() {
    this.patientPaymentService = new PatientPaymentService();
    this.patientInvestigationRequestService =
      new PatientInvestigationRequestService();
    this.paymentTypeSetupService = new PaymentTypeSetupService();
    this.patientReportService = new PatientReportService();
    this.commonService = new CommonService();

    this.paymentTypeSetupService
      .getAll()
      .then((data) => (this.paymentTypeList = data.data));

    var inv_request_id = localStorage.getItem('inv_request_id');
    if (inv_request_id) {
      this.patientPayment.investigation_no = inv_request_id;
      //localStorage.removeItem('investigation_id');
      this.findInvestigation();
    }

    var inv_request_no = localStorage.getItem('inv_request_no');
    if (inv_request_no) {
      this.patientPayment.investigation_no = inv_request_no;
      //localStorage.removeItem('investigation_id');
      this.findInvestigation();
    }
  },
  methods: {
    printAppointmentPayment(Id) {
      this.patientReportService.GetInvestigationPayment(Id, this).then((data) => (this.commonService.Downloadfile(data.data)));
    },
    paymentReset() {
      this.isPayment = true;
      this.patientPayment.ddlPaymentType = "";
      //this.isExist = "";
    },
    reset() {
      this.isPayment = true;
      this.addList = false;
      this.button = false;
      this.isCash = false;
      //this.isExist = "";
      //this.isPaid = "";
      this.isCashPaid = "";
      this.totalPtyAmount = 0;
      this.patientPayment = {
        cdpayment_date: moment(String(new Date())).format(
          "MM/DD/YYYY"
        ),
        ptypaid_amount: 0,
        total_amount_inf: 0,
        paymentTypeCash: "1",
        cash_paid_amount: 0,
        invoice_id: "",
        aPatient: {},
        lstPatientPaymentDetails: [],
        discount_amount: 0,
        discount_percent: 0,
      };
      this.submitted = false;

      localStorage.removeItem('inv_request_id');
      localStorage.removeItem('inv_request_no');
    },
    filterPaymentType() {
      return this.paymentTypeList.filter(function (el) {
        return el.payment_type_id != 1 && el.active == true;
      }, this);
    },
    getFormatted(data) {
      if (data.inv_due_amount == 0) {
        //this.isDueExist = "Not due!";
        this.$toast.add({
          severity: "warn",
          summary: "Warn Message",
          detail: "Not due!",
          life: 10000,
        });
        this.patientPayment = {
          paymentTypeCash: "1",
          invoice_id: "",
          aPatient: {},
          lstPatientPaymentDetails: [],
        };
      } else {
        //this.isDueExist = "";
        this.patientPayment.invoice_id = data.invoice_id;
        this.patientPayment.investigation_request_id = data.investigation_request_id;
        this.patientPayment.aPatient.patient_id = data.aPatient.patient_id;
        this.patientPayment.aPatient.first_name = data.aPatient.first_name;
        this.patientPayment.aPatient.contact_no = data.aPatient.contact_no;

        this.patientPayment.total_amount = data.inv_due_amount;
        this.patientPayment.cash_paid_amount = data.inv_due_amount;
        this.patientPayment.ptypaid_amount = data.inv_due_amount;
        this.patientPayment.discount_percent = 0;
        this.patientPayment.discount_amount = 0;
        this.patientPayment.discount_reason = data.discount_reason;

        this.patientPayment.cdinvestigation_request_date = moment(
          String(data.investigation_request_date)
        ).format("YYYY-MM-DD");
      }
    },
    findInvestigation() {
      this.patientInvestigationRequestService
        .getById(this.patientPayment.investigation_no)
        .then((data) => this.getFormatted(data.data));
    },

    paymentTypeIsExist() {
      var aPaymentType = this.patientPayment.lstPatientPaymentDetails.filter(
        function (el) {
          return (
            el.payment_type_id ===
            this.patientPayment.ddlPaymentType.payment_type_id
          );
        },
        this
      );

      if (aPaymentType.length) {
        //this.isExist = "This Payment Type already added";
        this.$toast.add({
          severity: "error",
          summary: "Error Message",
          detail: "This Payment Type already added",
          life: 10000,
        });
        this.isPayment = true;
        return;
      } else {
        //this.isExist = "";
        this.isPayment = false;
      }
    },
    addPaymentType() {
      this.submitted = true;
      var aPaymentType = this.patientPayment.lstPatientPaymentDetails.filter(
        function (el) {
          return (
            el.payment_type_id ===
            this.patientPayment.ddlPaymentType.payment_type_id
          );
        },
        this
      );

      if (aPaymentType.length) {
        //this.isExist = "This Payment Type already added";
        this.$toast.add({
          severity: "error",
          summary: "Error Message",
          detail: "This Payment Type already added",
          life: 10000,
        });
        //this.isPayment = true;
        return;
      } else {
        if (!this.patientPayment.aPatient.first_name) {
          return;
        } else if (!this.patientPayment.cdpayment_date) {
          this.$toast.add({
            severity: "error",
            summary: "Error Message",
            detail: "Payment Date is required",
            life: 10000,
          });
          return;
        } else if (!this.patientPayment.ddlPaymentType) {
          //this.isExist = "Payment Type is required.";
          this.$toast.add({
            severity: "error",
            summary: "Error Message",
            detail: "Payment Type is required",
            life: 10000,
          });
          return;
        } else if (
          this.patientPayment.ptypaid_amount <= 0 ||
          !this.patientPayment.ptypaid_amount
        ) {
          //this.isPaid = "Non-Cash Paid Amt is required";
          this.$toast.add({
            severity: "error",
            summary: "Error Message",
            detail: "Non-Cash Paid Amt is required",
            life: 10000,
          });
          return;
        } else if (
          this.patientPayment.cash_paid_amount <= 0 ||
          this.patientPayment.cash_paid_amount <
          this.patientPayment.ptypaid_amount
        ) {
          //this.isPaid = "Exceed amount!";
          this.$toast.add({
            severity: "warn",
            summary: "Warn Message",
            detail: "Exceed amount!",
            life: 10000,
          });
          return;
        } else {
          //this.isExist = "";
          //this.isPaid = "";

          var obj = {
            payment_type_id: this.patientPayment.ddlPaymentType.payment_type_id,
            _payment_type_name:
              this.patientPayment.ddlPaymentType.payment_type_name,

            paid_amount: parseInt(this.patientPayment.ptypaid_amount),
          };

          this.patientPayment.lstPatientPaymentDetails.push(obj);

          // this.totalPtyAmount =
          //   this.totalPtyAmount + parseInt(this.patientPayment.ptypaid_amount);

          // this.patientPayment.cash_paid_amount =
          //   this.patientPayment.total_amount - this.totalPtyAmount;
          this.gridSum();
        }
      }

      this.addList = true;
      this.button = true;
    },

    deleteFromList(data) {
      this.patientPayment.lstPatientPaymentDetails =
        this.patientPayment.lstPatientPaymentDetails.filter(function (el) {
          return el.payment_type_id != data.payment_type_id;
        }, this);

      // this.patientPayment.cash_paid_amount =
      //   this.patientPayment.cash_paid_amount + parseInt(data.paid_amount);

      if (!this.patientPayment.lstPatientPaymentDetails.length) {
        this.addList = false;
        this.button = false;
      }
      this.gridSum();
    },
    gridSum() {
      this.patientPayment.total_amount_inf = 0;
      this.patientPayment.lstPatientPaymentDetails.forEach((element) => {
        this.patientPayment.total_amount_inf += element.paid_amount;
      });
      this.patientPayment.cash_paid_amount =
        this.patientPayment.total_amount - this.patientPayment.total_amount_inf - this.patientPayment.discount_amount;

      this.dueCalculate();
    },
    dueCalculate() {
      this.patientPayment.due_amount =
        this.patientPayment.total_amount -
        this.patientPayment.cash_paid_amount -
        this.patientPayment.total_amount_inf -
        this.patientPayment.discount_amount;

      if (
        this.patientPayment.total_amount < this.patientPayment.cash_paid_amount
      ) {
        this.isCashPaid = "Exceed amount!";
        this.$toast.add({
          severity: "warn",
          summary: "Warn Message",
          detail: "Exceed amount!",
          life: 10000,
        });
      } else {
        this.isCashPaid = "";
      }
    },
    calculateChangeAmount() {
      if (this.patientPayment.cash_amount.length) {
        this.patientPayment.change_amount =
          this.patientPayment.cash_amount -
          this.patientPayment.cash_paid_amount;
      } else {
        this.patientPayment.change_amount = 0;
      }
    },
    getDiscountPercent() {
      this.patientPayment.discount_percent = Math.ceil(
        (this.patientPayment.discount_amount /
          this.patientPayment.total_amount) *
        100);

      this.gridSum();
    },
    getDiscountAmount() {
      this.patientPayment.discount_amount =
        (this.patientPayment.total_amount *
          this.patientPayment.discount_percent) /
        100;

      this.gridSum();
    },
    async add() {
      this.submitted = true;
      try {
        if (!this.patientPayment.aPatient.first_name) {
          return;
        } else if (!this.patientPayment.cdpayment_date) {
          this.$toast.add({
            severity: "error",
            summary: "Error Message",
            detail: "Payment Date is required",
            life: 10000,
          });
          return;
        } else if (!this.patientPayment.invoice_id) {
          return;
        } else if (this.patientPayment.cash_paid_amount <= 0) {
          this.$toast.add({
            severity: "error",
            summary: "Error",
            detail: "Enter amount!",
            life: 10000,
          });
          return;
        } else if (this.patientPayment.discount_amount > 0 && this.patientPayment.due_amount > 0) {
          this.$toast.add({
            severity: "error",
            summary: "Error",
            detail: "Cannot discount with due amount!",
            life: 10000,
          });
          return;
        } else if (this.isCashPaid) {
          this.$toast.add({
            severity: "warn",
            summary: "Warn Message",
            detail: "Exceed amount!",
            life: 10000,
          });
          return;
        } else {
          this.patientPayment.payment_date = moment(
            String(this.patientPayment.cdpayment_date)
          ).format("YYYY-MM-DD");

          if (this.patientPayment.cash_paid_amount > 0 && !this.isCash) {
            this.patientPayment.lstPatientPaymentDetails.push({
              payment_type_id: this.patientPayment.paymentTypeCash,

              paid_amount: this.patientPayment.cash_paid_amount,
            });

            this.isCash = true;
          }
          this.patientPayment.paid_amount =
            parseInt(this.patientPayment.cash_paid_amount) +
            parseInt(this.patientPayment.total_amount_inf);

          this.patientPayment.discount_amount = this.patientPayment.discount_amount == "" ? 0 : this.patientPayment.discount_amount;
          this.patientPayment.discount_percent = this.patientPayment.discount_percent == "" ? 0 : this.patientPayment.discount_percent;
          let result = await this.patientPaymentService.addInvestigation(
            this.patientPayment, this
          );
          if (result.status === 200) {
            this.$toast.add({
              severity: "success",
              summary: "Success",
              detail: "Saved Successfully",
              life: 10000,
            });
            this.printAppointmentPayment(result.data);
            var inv_request_id = localStorage.getItem('inv_request_id');
            if (inv_request_id) {
              localStorage.removeItem('inv_request_id');
              this.$router.push("/patient-investigation-request");
            }
            var inv_request_no = localStorage.getItem('inv_request_no');
            if (inv_request_no) {
              localStorage.removeItem('inv_request_no');
              this.$router.push("/investigation-result-dispatch");
            }
            this.reset();
          } else if (result.status === 401) {
            this.$toast.add({
              severity: "error",
              summary: "Error",
              detail: result.data,
              life: 10000,
            });
          } else {
            this.$toast.add({
              severity: "error",
              summary: "Failed",
              detail: result.res,
              life: 10000,
            });
            this.reset();
          }
        }
      } catch (error) {
        this.$toast.add({
          severity: "error",
          summary: "Failed",
          detail: error,
          life: 10000,
        });
        this.reset();
      }
    },
  },
};
