import ConsultancyDepartmentSetupService from "../../../service/setup/ConsultancyDepartmentSetupService";
import DoctorSetupService from "../../../service/setup/DoctorSetupService";
import CommissionSetupService from "../../../service/setup/commission/DoctorCommissionSetupService";
import { FilterMatchMode, FilterOperator } from "primevue/api";

export default {
    data() {
        return {
            dataList: false,
            button: false,
            departmentList: [],
            doctorList: [],
            commissionServiceList: [],
            commissionServicePackageList: [],
            commissionSetup: {
                ipdType: "Service",
                type: "All",
                commissionPercent: "0",
                commissionAmount: "0",
                lstDoctor: [],
                lstService: [],
                lstServicePackage: []
            },
            filters: {
                global: { value: null, matchMode: FilterMatchMode.CONTAINS },
                name: { value: null, matchMode: FilterMatchMode.STARTS_WITH },
            },
            initFilters() {
                this.filters = {
                    global: { value: null, matchMode: FilterMatchMode.CONTAINS },
                    name: {
                        operator: FilterOperator.AND,
                        constraints: [
                            { value: null, matchMode: FilterMatchMode.STARTS_WITH },
                        ],
                    },
                };
            },
            submitted: false,
        };
    },
    created() {
        this.doctorSetupService = new DoctorSetupService();
        this.consultancyDepartmentSetupService = new ConsultancyDepartmentSetupService();
        this.commissionSetupService = new CommissionSetupService();

        this.consultancyDepartmentSetupService
            .getActiveCombo()
            .then((data) => (this.departmentList = data.data));

        this.doctorSetupService
            .getAll()
            .then((data) => this.mergeDoctorInfo(data.data));
    },
    methods: {
        hideNew() {
            this.commissionSetup = {
                ipdType: "Service",
                type: "All",
                commissionPercent: "0",
                commissionAmount: "0",
                lstDoctor: [],
                lstService: [],
                lstServicePackage: []
            };
            this.dataList = false;
            this.button = false;
            this.submitted = false;
        },
        mergeDoctorInfo(data) {
            data.forEach((element) => {
                this.doctorList.push({
                    department_id: element.department_id,
                    doctor_id: element.doctor_id,
                    doctor_name: element.doc_no + "-" + element.doctor_name,
                });
            });
        },
        filterDoctorsDepartmentWise() {
            return this.doctorList.filter(function (el) {
                return el.department_id === this.commissionSetup.department_id;
            }, this);
        },
        onChange() {
            if (this.commissionSetup.type == "All") {
                this.commissionSetup.doctor_id = "All";
            } else {
                this.commissionSetup.doctor_id = "";
            }
            this.getCommissionByDoctor();
        },
        getCommissionByDoctor() {
            this.dataList = false;
            this.button = false;
            this.submitted = true;
            if (!this.commissionSetup.department_id && this.commissionSetup.type == "Single") {
                return;
            } else if (!this.commissionSetup.doctor_id && this.commissionSetup.type == "Single") {
                return;
            } else {
                if (!this.commissionSetup.doctor_id && this.commissionSetup.type == "All") {
                    this.commissionSetup.doctor_id = "All";
                }
                this.dataList = true;
                this.button = true;
                if (this.commissionSetup.ipdType == 'Service') {
                    this.commissionSetupService
                        .getIPDServiceCommissionByDoctor(this.commissionSetup.doctor_id, this)
                        .then((data) => (this.commissionServiceList = data.data));
                    this.commissionServicePackageList = [];
                } else if (this.commissionSetup.ipdType == 'Package') {
                    this.commissionSetupService
                        .getIPDServicePackageCommissionByDoctor(this.commissionSetup.doctor_id, this)
                        .then((data) => (this.commissionServicePackageList = data.data));
                    this.commissionServiceList = [];
                }
            }
            this.submitted = false;
        },
        updateCommissionPercentAmount() {
            this.submitted = true;
            if (!this.commissionSetup.commissionPercent) {
                return;
            }
            if (!this.commissionSetup.commissionAmount) {
                return;
            }

            if (this.commissionSetup.ipdType == 'Service') {
                this.commissionServiceList.forEach(element => {
                    element._commission_rate_percentage = this.commissionSetup.commissionPercent,
                        element._commission_rate_amount = this.commissionSetup.commissionAmount
                });
            } else if (this.commissionSetup.ipdType == 'Package') {
                this.commissionServicePackageList.forEach(element => {
                    element._commission_rate_percentage = this.commissionSetup.commissionPercent,
                        element._commission_rate_amount = this.commissionSetup.commissionAmount
                });
            }
            this.submitted = false;
        },
        checkAllCommissionRatePercentage(){
            if(this.commissionSetup.commissionPercent > 0){
                this.commissionSetup.commissionAmount = "0";
            }
        },
        checkAllCommissionRateAmount(){
            if(this.commissionSetup.commissionAmount > 0){
                this.commissionSetup.commissionPercent = "0";
            }
        },
        checkCommissionRatePercentage(data) {
            if (this.commissionSetup.ipdType == 'Service') {
                this.commissionServiceList.forEach(
                    (element) => {
                        if (element.service_id == data.service_id) {
                            if (element._commission_rate_percentage > 0)
                                element._commission_rate_amount = 0;
                        }
                    }
                );
            } else if (this.commissionSetup.ipdType == 'Package') {
                this.commissionServicePackageList.forEach(
                    (element) => {
                        if (element.service_package_id == data.service_package_id) {
                            if (element._commission_rate_percentage > 0)
                                element._commission_rate_amount = 0;
                        }
                    }
                );
            }
        },
        checkCommissionRateAmount(data) {
            if (this.commissionSetup.ipdType == 'Service') {
                this.commissionServiceList.forEach(
                    (element) => {
                        if (element.service_id == data.service_id) {
                            if (element._commission_rate_amount > 0)
                                element._commission_rate_percentage = 0;
                        }
                    }
                );
            } else if (this.commissionSetup.ipdType == 'Package') {
                this.commissionServicePackageList.forEach(
                    (element) => {
                        if (element.service_package_id == data.service_package_id) {
                            if (element._commission_rate_amount > 0)
                                element._commission_rate_percentage = 0;
                        }
                    }
                );
            }
        },
        async add() {
            this.submitted = true;
            try {
                if (this.commissionSetup.ipdType == 'Service') {
                    this.requred = "";
                    this.commissionServiceList.forEach((element) => {
                        if (element._commission_rate_amount > 0 && element._commission_rate_percentage > 0) {
                            this.requred = "required";
                        }
                    });

                    if (this.requred) {
                        this.$toast.add({
                            severity: "error",
                            summary: "Required",
                            detail: "Cannot enter commission (%) and commission (tk) at the same time",
                            life: 10000,
                        });
                        return;
                    } else {
                        if (this.commissionSetup.doctor_id == "All") {
                            this.commissionSetup.lstDoctor = this.doctorList;
                        } else {
                            this.commissionSetup.lstDoctor.push({
                                department_id: "",
                                doctor_id: this.commissionSetup.doctor_id,
                                doctor_name: "",
                            });
                        }
                        if (this.commissionServiceList.length > 0) {
                            this.commissionSetup.lstService = this.commissionServiceList;

                            let result = await this.commissionSetupService.iPDServiceCommissionSave(
                                this.commissionSetup, this
                            );
                            if (result.status === 201) {
                                this.$toast.add({
                                    severity: "success",
                                    summary: "Success",
                                    detail: "Saved Successfully",
                                    life: 10000,
                                });
                                this.hideNew();
                            } else {
                                this.$toast.add({
                                    severity: "error",
                                    summary: "Failed",
                                    detail: result.res,
                                    life: 10000,
                                });
                            }
                        } else {
                            this.$toast.add({
                                severity: "error",
                                summary: "Required",
                                detail: "Service list empty",
                                life: 10000,
                            });
                            return;
                        }
                    }
                } else if (this.commissionSetup.ipdType == 'Package') {
                    this.requred = "";
                    this.commissionServicePackageList.forEach((element) => {
                        if (element._commission_rate_amount > 0 && element._commission_rate_percentage > 0) {
                            this.requred = "required";
                        }
                    });

                    if (this.requred) {
                        this.$toast.add({
                            severity: "error",
                            summary: "Required",
                            detail: "Cannot enter commission (%) and commission (tk) at the same time",
                            life: 10000,
                        });
                        return;
                    } else {
                        if (this.commissionSetup.doctor_id == "All") {
                            this.commissionSetup.lstDoctor = this.doctorList;
                        } else {
                            this.commissionSetup.lstDoctor.push({
                                department_id: "",
                                doctor_id: this.commissionSetup.doctor_id,
                                doctor_name: "",
                            });
                        }

                        if (this.commissionServicePackageList.length > 0) {
                            this.commissionSetup.lstServicePackage = this.commissionServicePackageList;
                            let result = await this.commissionSetupService.iPDServicePackageCommissionSave(
                                this.commissionSetup, this
                            );
                            if (result.status === 201) {
                                this.$toast.add({
                                    severity: "success",
                                    summary: "Success",
                                    detail: "Saved Successfully",
                                    life: 10000,
                                });
                                this.hideNew();
                            } else {
                                this.$toast.add({
                                    severity: "error",
                                    summary: "Failed",
                                    detail: result.res,
                                    life: 10000,
                                });
                            }
                        } else {
                            this.$toast.add({
                                severity: "error",
                                summary: "Required",
                                detail: "Package list empty",
                                life: 10000,
                            });
                            return;
                        }
                    }
                }
            } catch (error) {
                this.$toast.add({
                    severity: "error",
                    summary: "Failed",
                    detail: error,
                    life: 10000,
                });
            }
        },
    },
};
