import DoctorAppointmentService from "../../service/front-desk/DoctorAppointmentService";
import AppointmentPrescriptionService from "../../service/doctor-panel/AppointmentPrescriptionService";
import DoctorSetupService from "../../service/setup/DoctorSetupService";
import PatientInvestigationRequestService from "../../service/front-desk/PatientInvestigationRequestService";
import PatientRegistrationService from "../../service/front-desk/PatientRegistrationService";
import CommonService from "../../service/CommonService";
import DiagnosisReportService from "../../service/report/DiagnosisReportService";
import DashboardService from "../../service/components/DashboardService";
import moment from "moment";
import { ref } from 'vue';

export default {
    data() {
        return {
            isDoctor: false,
            isAdmin: false,
            todaysAppointmentCount: 0,
            todaysAppointmentList: [],
            visitedAppointmentCount: 0,
            visitedAppointmentList: [],
            upcomingAppointmentCount: 0,
            upcomingAppointmentList: [],
            scheduleList: [],
            investigationResultList: [],
            totalDoctor: 0,
            totalPatient: 0,
            totalAssistant: 0,
            totalEmployee: 0,
            topInvestigationList: [],
            topDrConsultancyList: [],
            marketingTeamReferredByList: [],
            activeAppointmentCount: 0,
            expiredAppointmentCount: 0,

            // chart
            chartData: {},
            lightOptions: {},
            basicData: {},
            basicOptions: {},
            genderData: {},
            genderOptions: {},
            submitted: false,
        };
    },
    created() {
        this.appointmentPrescriptionService = new AppointmentPrescriptionService();
        this.doctorAppointmentService = new DoctorAppointmentService();
        this.doctorSetupService = new DoctorSetupService();
        this.patientInvestigationRequestService = new PatientInvestigationRequestService();
        this.patientRegistrationService = new PatientRegistrationService();
        this.commonService = new CommonService();
        this.diagnosisReportService = new DiagnosisReportService();
        this.dashboardService = new DashboardService();

        var fromDate = "2021-01-01";
        var toDate = moment(String(new Date(new Date().setDate(new Date().getDate() + 7)))).format("YYYY-MM-DD");
        var status = "All";
        this.userInfo = JSON.parse(localStorage.getItem("userInfo"));
        if (this.userInfo.role_id == "2" && this.userInfo.doctor_id) {
            this.doctorAppointmentService
                .getDynamicDoctorWise(
                    fromDate,
                    toDate,
                    status,
                    this.userInfo.doctor_id
                )
                .then((data) => this.formatted(data.data));

            this.getDrSchedule(this.userInfo.doctor_id);
            this.getInvestigationResult(this.userInfo.doctor_id);

            this.isDoctor = true;
        } else if (this.userInfo.role_id == "1") {
            this.dashboardService.getAllAdmin()
                .then((data) => this.getFormattedRegistered(data.data));

            this.doctorAppointmentService
                .getDynamic(
                    fromDate,
                    toDate,
                    status,
                    status
                )
                .then((data) => this.formattedAppointment(data.data));

            const d = new Date();
            let year = d.getFullYear();
            var iToDate = moment(String(new Date())).format("YYYY-MM-DD");
            this.patientInvestigationRequestService
                .getDynamic(
                    year + "-01-01",
                    iToDate,
                    status,
                    status
                )
                .then((data) => this.formattedInvestigation(data.data));

            this.patientRegistrationService.getAll()
                .then((data) => this.getFormattedRegisteredPatient(data.data));
            this.isAdmin = true;
        }
    },
    methods: {
        prescription(data) {
            localStorage.setItem("prescriptionData", JSON.stringify(data));
            this.$router.push("/appointment-prescription");
        },
        formatted(data) {
            this.todaysAppointment(data);
            this.visitedAppointment(data);
            this.upcomingAppointment(data);
        },
        todaysAppointment(data) {
            var todaysData = data.filter(function (el) {
                return (
                    el.appoinment_status == "Active" && moment(String(el.appoint_date)).format("YYYY-MM-DD") == moment(String(new Date())).format("YYYY-MM-DD"));
            }, this);
            this.todaysAppointmentList = todaysData;
            this.todaysAppointmentCount = todaysData.length;
        },
        visitedAppointment(data) {
            var visitedData = data.filter(function (el) {
                return (
                    el.appoinment_status == "Visited");
            }, this);
            this.visitedAppointmentList = visitedData;
            this.visitedAppointmentCount = visitedData.length;
        },
        upcomingAppointment(data) {
            var upcomingData = data.filter(function (el) {
                return (
                    el.appoinment_status == "Active" && moment(String(el.appoint_date)).format("YYYY-MM-DD") <= moment(String(new Date(new Date().setDate(new Date().getDate() + 7)))).format("YYYY-MM-DD"));
            }, this);
            this.upcomingAppointmentList = upcomingData;
            this.upcomingAppointmentCount = upcomingData.length;
        },
        getFormattedSchedule(data) {
            data.forEach((element) => {
                element.start_time1 = moment(
                    String(new Date("2000-01-01 " + element.start_time1))
                ).format("h:mm a");
                element.end_time1 = moment(
                    String(new Date("2000-01-01 " + element.end_time1))
                ).format("h:mm a");
                element.start_time2 = moment(
                    String(new Date("2000-01-01 " + element.start_time2))
                ).format("h:mm a");
                element.end_time2 = moment(
                    String(new Date("2000-01-01 " + element.end_time2))
                ).format("h:mm a");
            });

            this.scheduleList = data;
        },
        getDrSchedule(doctorId) {
            this.doctorSetupService
                .getDoctorScheduleById(doctorId)
                .then((data) => this.getFormattedSchedule(data.data));
        },
        getInvestigationResult(doctorId) {
            var fromData = moment(String(new Date(new Date().setDate(new Date().getDate() - 30)))).format("YYYY-MM-DD");
            var toDate = moment(String(new Date())).format("YYYY-MM-DD");
            this.patientInvestigationRequestService.refferedDocWise(fromData, toDate, doctorId).then((data) => this.investigationResultList = data.data);
        },
        reportPrint(barcode_or_level, template_name) {
            this.diagnosisReportService.GetInvestigationReportTemplateWise(barcode_or_level, template_name, this).then((data) => this.commonService.Downloadfile(data.data));
        },
        //
        formatDate(value) {
            return moment(String(value)).format("MM/DD/YYYY h:mm:ss a");
        },
        formatDate2(value) {
            return moment(String(value)).format("MM/DD/YYYY");
        },
        getFormattedRegistered(data) {
            this.totalDoctor = data.total_doctor;
            this.totalPatient = data.total_patient;
            this.totalAssistant = data.total_doctor_assistant;
            this.totalEmployee = data.total_employee;
            this.topInvestigationList = JSON.parse(data.investigation_request_count);
            this.topDrConsultancyList = JSON.parse(data.doctor_appointment_request_count);
            this.marketingTeamReferredByList = JSON.parse(data.marketing_team_reffered_by_count);
        },
        formattedAppointment(data) {
            var activeData = data.filter(function (el) {
                return (
                    el.appoinment_status == "Active");
            }, this);
            this.activeAppointmentCount = activeData.length;

            var visitedData = data.filter(function (el) {
                return (
                    el.appoinment_status == "Visited");
            }, this);
            this.visitedAppointmentCount = visitedData.length;

            var expiredData = data.filter(function (el) {
                return (
                    el.appoinment_status == "Expired");
            }, this);
            this.expiredAppointmentCount = expiredData.length;

            // chart
            this.chartData = ref({
                labels: ['Active', 'Visited', 'Expired'],
                datasets: [
                    {
                        data: [this.activeAppointmentCount, this.visitedAppointmentCount, this.expiredAppointmentCount],
                        backgroundColor: ["#42A5F5", "#66BB6A", "#FFA726"],
                        hoverBackgroundColor: ["#64B5F6", "#81C784", "#FFB74D"]
                    }
                ]
            });
            this.lightOptions = ref({
                plugins: {
                    legend: {
                        labels: {
                            color: '#495057'
                        }
                    }
                }
            });
        },
        formattedInvestigation(data) {
            const d = new Date();
            let year = d.getFullYear();
            var janFromDate = new Date(year + "-01-01");
            var janToDate = new Date(year + "-01-31");
            var febFromDate = new Date(year + "-02-01");
            var febToDate = new Date(year + "-02-28");
            var marFromDate = new Date(year + "-03-01");
            var marToDate = new Date(year + "-03-31");
            var aprFromDate = new Date(year + "-04-01");
            var aprToDate = new Date(year + "-04-30");
            var mayFromDate = new Date(year + "-05-01");
            var mayToDate = new Date(year + "-05-31");
            var junFromDate = new Date(year + "-06-01");
            var junToDate = new Date(year + "-06-30");
            var julFromDate = new Date(year + "-07-01");
            var julToDate = new Date(year + "-07-31");
            var augFromDate = new Date(year + "-08-01");
            var augToDate = new Date(year + "-08-31");
            var sepFromDate = new Date(year + "-09-01");
            var sepToDate = new Date(year + "-09-30");
            var octFromDate = new Date(year + "-10-01");
            var octToDate = new Date(year + "-10-31");
            var novFromDate = new Date(year + "-11-01");
            var novToDate = new Date(year + "-11-30");
            var decFromDate = new Date(year + "-12-01");
            var decToDate = new Date(year + "-12-31");

            var janPendingData = data.filter(function (el) {
                return (
                    janToDate >= new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) && new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) >= janFromDate && el.investigationDeliveryStatus == "Delivery Pending");
            }, this);
            this.janPendingCount = janPendingData.length;

            var janCompletedData = data.filter(function (el) {
                return (
                    janToDate >= new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) && new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) >= janFromDate && el.investigationDeliveryStatus == "Completed");
            }, this);
            this.janCompletedCount = janCompletedData.length;

            var febPendingData = data.filter(function (el) {
                return (
                    febToDate >= new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) && new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) >= febFromDate && el.investigationDeliveryStatus == "Delivery Pending");
            }, this);
            this.febPendingCount = febPendingData.length;

            var febCompletedData = data.filter(function (el) {
                return (
                    febToDate >= new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) && new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) >= febFromDate && el.investigationDeliveryStatus == "Completed");
            }, this);
            this.febCompletedCount = febCompletedData.length;

            var marPendingData = data.filter(function (el) {
                return (
                    marToDate >= new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) && new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) >= marFromDate && el.investigationDeliveryStatus == "Delivery Pending");
            }, this);
            this.marPendingCount = marPendingData.length;

            var marCompletedData = data.filter(function (el) {
                return (
                    marToDate >= new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) && new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) >= marFromDate && el.investigationDeliveryStatus == "Completed");
            }, this);
            this.marCompletedCount = marCompletedData.length;

            var aprPendingData = data.filter(function (el) {
                return (
                    aprToDate >= new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) && new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) >= aprFromDate && el.investigationDeliveryStatus == "Delivery Pending");
            }, this);
            this.aprPendingCount = aprPendingData.length;

            var aprCompletedData = data.filter(function (el) {
                return (
                    aprToDate >= new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) && new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) >= aprFromDate && el.investigationDeliveryStatus == "Completed");
            }, this);
            this.aprCompletedCount = aprCompletedData.length;

            var mayPendingData = data.filter(function (el) {
                return (
                    mayToDate >= new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) && new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) >= mayFromDate && el.investigationDeliveryStatus == "Delivery Pending");
            }, this);
            this.mayPendingCount = mayPendingData.length;

            var mayCompletedData = data.filter(function (el) {
                return (
                    mayToDate >= new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) && new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) >= mayFromDate && el.investigationDeliveryStatus == "Completed");
            }, this);
            this.mayCompletedCount = mayCompletedData.length;

            var junPendingData = data.filter(function (el) {
                return (
                    junToDate >= new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) && new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) >= junFromDate && el.investigationDeliveryStatus == "Delivery Pending");
            }, this);
            this.junPendingCount = junPendingData.length;

            var junCompletedData = data.filter(function (el) {
                return (
                    junToDate >= new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) && new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) >= junFromDate && el.investigationDeliveryStatus == "Completed");
            }, this);
            this.junCompletedCount = junCompletedData.length;

            var julPendingData = data.filter(function (el) {
                return (
                    julToDate >= new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) && new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) >= julFromDate && el.investigationDeliveryStatus == "Delivery Pending");
            }, this);
            this.julPendingCount = julPendingData.length;

            var julCompletedData = data.filter(function (el) {
                return (
                    julToDate >= new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) && new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) >= julFromDate && el.investigationDeliveryStatus == "Completed");
            }, this);
            this.julCompletedCount = julCompletedData.length;

            var augPendingData = data.filter(function (el) {
                return (
                    augToDate >= new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) && new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) >= augFromDate && el.investigationDeliveryStatus == "Delivery Pending");
            }, this);
            this.augPendingCount = augPendingData.length;

            var augCompletedData = data.filter(function (el) {
                return (
                    augToDate >= new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) && new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) >= augFromDate && el.investigationDeliveryStatus == "Completed");
            }, this);
            this.augCompletedCount = augCompletedData.length;

            var sepPendingData = data.filter(function (el) {
                return (
                    sepToDate >= new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) && new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) >= sepFromDate && el.investigationDeliveryStatus == "Delivery Pending");
            }, this);
            this.sepPendingCount = sepPendingData.length;

            var sepCompletedData = data.filter(function (el) {
                return (
                    sepToDate >= new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) && new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) >= sepFromDate && el.investigationDeliveryStatus == "Completed");
            }, this);
            this.sepCompletedCount = sepCompletedData.length;

            var octPendingData = data.filter(function (el) {
                return (
                    octToDate >= new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) && new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) >= octFromDate && el.investigationDeliveryStatus == "Delivery Pending");
            }, this);
            this.octPendingCount = octPendingData.length;

            var octCompletedData = data.filter(function (el) {
                return (
                    octToDate >= new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) && new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) >= octFromDate && el.investigationDeliveryStatus == "Completed");
            }, this);
            this.octCompletedCount = octCompletedData.length;

            var novPendingData = data.filter(function (el) {
                return (
                    novToDate >= new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) && new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) >= novFromDate && el.investigationDeliveryStatus == "Delivery Pending");
            }, this);
            this.novPendingCount = novPendingData.length;

            var novCompletedData = data.filter(function (el) {
                return (
                    novToDate >= new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) && new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) >= novFromDate && el.investigationDeliveryStatus == "Completed");
            }, this);
            this.novCompletedCount = novCompletedData.length;

            var decPendingData = data.filter(function (el) {
                return (
                    decToDate >= new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) && new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) >= decFromDate && el.investigationDeliveryStatus == "Delivery Pending");
            }, this);
            this.decPendingCount = decPendingData.length;

            var decCompletedData = data.filter(function (el) {
                return (
                    decToDate >= new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) && new Date(moment(String(el.investigation_request_date)).format("YYYY-MM-DD")) >= decFromDate && el.investigationDeliveryStatus == "Completed");
            }, this);
            this.decCompletedCount = decCompletedData.length;

            // chart
            this.basicData = ref(
                {
                    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'],
                    datasets: [
                        {
                            label: 'Pending',
                            data: [this.janPendingCount, this.febPendingCount, this.marPendingCount, this.aprPendingCount, this.mayPendingCount, this.junPendingCount, this.julPendingCount, this.augPendingCount, this.sepPendingCount, this.octPendingCount, this.novPendingCount, this.decPendingCount],
                            fill: false,
                            backgroundColor: '#2f4860',
                            borderColor: '#2f4860',
                            tension: .4
                        },
                        {
                            label: 'Delivered',
                            data: [this.janCompletedCount, this.febCompletedCount, this.marCompletedCount, this.aprCompletedCount, this.mayCompletedCount, this.junCompletedCount, this.julCompletedCount, this.augCompletedCount, this.sepCompletedCount, this.octCompletedCount, this.novCompletedCount, this.decCompletedCount],
                            fill: false,
                            backgroundColor: '#00bb7e',
                            borderColor: '#00bb7e',
                            tension: .4
                        }
                    ]
                }
            );
            this.basicOptions = ref(
                {
                    plugins: {
                        legend: {
                            labels: {
                                color: '#495057'
                            }
                        }
                    },
                    scales: {
                        x: {
                            ticks: {
                                color: '#495057'
                            },
                            grid: {
                                color: '#ebedef'
                            }
                        },
                        y: {
                            ticks: {
                                color: '#495057'
                            },
                            grid: {
                                color: '#ebedef'
                            }
                        }
                    }
                }
            );
        },
        getFormattedRegisteredPatient(data){
            var maleData = data.filter(function (el) {
                return (
                    el.gender == "Male");
            }, this);
            this.maleCount = maleData.length;

            var femaleData = data.filter(function (el) {
                return (
                    el.gender == "Female");
            }, this);
            this.femaleCount = femaleData.length;

            var thirdData = data.filter(function (el) {
                return (
                    el.gender == "Third");
            }, this);
            this.thirdCount = thirdData.length;

            // chart
            this.genderData = ref({
                labels: ['Male','Female','Third'],
                datasets: [
                    {
                        data: [this.maleCount, this.femaleCount, this.thirdCount],
                        backgroundColor: ["#36A2EB","#FF6384","#FFCE56"],
                        hoverBackgroundColor: ["#36A2EB","#FF6384","#FFCE56"]
                    }
                ]
            });
    
            this.genderOptions = ref({
                plugins: {
                    legend: {
                        labels: {
                            color: '#495057'
                        }
                    }
                }
            });
        }
    },
};
