import PatientPaymentService from "../../service/cash-counter/PatientPaymentService";
import PaymentTypeSetupService from "../../service/setup/PaymentTypeSetupService";
import IPDReportService from "../../service/report/IPDReportService";
import CommonService from "../../service/CommonService";
import moment from "moment";
import { FilterMatchMode, FilterOperator } from "primevue/api";

export default {
  data() {
    return {
      isPayment: true,
      //addList: false,
      button: false,
      isCash: false,
      //isPaid: "",
      isCashPaid: "",
      //isDueExist: "",
      //isExist: "",
      paymentTypeList: [],
      totalPtyAmount: 0,
      admission_no: "",
      patientPayment: {
        cdpayment_date: moment(String(new Date())).format(
          "MM/DD/YYYY"
        ),
        total_amount: 0,
        ptypaid_amount: 0,
        total_amount_inf: 0,
        paymentTypeCash: "1",
        invoice_id: "",
        aPatient: {},
        lstPatientPaymentDetails: [],
        discount_amount: 0,
        discount_percent: 0,
        refund_amount: 0,
        isRefund: false,
        ipdServiceList: [],
      },
      filters: {
        global: { value: null, matchMode: FilterMatchMode.CONTAINS },
        name: { value: null, matchMode: FilterMatchMode.STARTS_WITH },
      },
      initFilters() {
        this.filters = {
          global: { value: null, matchMode: FilterMatchMode.CONTAINS },
          name: {
            operator: FilterOperator.AND,
            constraints: [
              { value: null, matchMode: FilterMatchMode.STARTS_WITH },
            ],
          },
        };
      },
      submitted: false,
    };
  },
  created() {
    this.patientPaymentService = new PatientPaymentService();
    this.paymentTypeSetupService = new PaymentTypeSetupService();
    this.iPDReportService = new IPDReportService();
    this.commonService = new CommonService();

    this.paymentTypeSetupService
      .getAll()
      .then((data) => (this.paymentTypeList = data.data));

    var admission_id_for_payment = localStorage.getItem('admission_id_for_payment');
    if (admission_id_for_payment) {

      this.admission_no = admission_id_for_payment;
      //localStorage.removeItem('admission_id_for_payment');
      this.findIPDPaymentInfo();
    }

    var admission_no_for_payment_from_admissionlist = localStorage.getItem('admission_no_for_payment_from_admissionlist');
    if (admission_no_for_payment_from_admissionlist) {
      this.admission_no = admission_no_for_payment_from_admissionlist;
      //localStorage.removeItem('admission_no_for_payment_from_admissionlist');
      this.findIPDPaymentInfo();
    }
  },
  methods: {
    printAdmissionPayment(Id) {
      this.iPDReportService.GetAdmissionPayment(Id, this).then((data) => (this.commonService.Downloadfile(data.data)));
    },
    printDischargeBillSummary(Id) {
      this.iPDReportService.GetDischargeBillSummary(Id, this).then((data) => (this.commonService.Downloadfile(data.data)));
    },
    paymentReset() {
      this.isPayment = true;
      this.patientPayment.ddlPaymentType = "";
      //this.isExist = "";
    },
    reset() {
      this.isPayment = true;
      //this.addList = false;
      this.button = false;
      this.isCash = false;
      //this.isExist = "";
      //this.isPaid = "";
      this.isCashPaid = "";
      this.totalPtyAmount = 0;
      this.patientPayment = {
        cdpayment_date: moment(String(new Date())).format(
          "MM/DD/YYYY"
        ),
        total_amount: 0,
        ptypaid_amount: 0,
        total_amount_inf: 0,
        paymentTypeCash: "1",
        cash_paid_amount: 0,
        invoice_id: "",
        aPatient: {},
        lstPatientPaymentDetails: [],
        discount_amount: 0,
        discount_percent: 0,
        refund_amount: 0,
        isRefund: false,
        ipdServiceList: [],
      };
      this.submitted = false;
      localStorage.removeItem('admission_id_for_payment');
      localStorage.removeItem('admission_no_for_payment_from_admissionlist');
    },
    filterPaymentType() {
      return this.paymentTypeList.filter(function (el) {
        return el.payment_type_id != 1 && el.active == true;
      }, this);
    },
    getFormatted(obj) {
      var data = JSON.parse(obj.data);
      //this.reset();
      if (data.length == 0) {
        // this.isDueExist = "Not due!";
        this.$toast.add({
          severity: "warn",
          summary: "Warn Message",
          detail: "Not due!",
          life: 10000,
        });
        this.patientPayment = {
          cdpayment_date: moment(String(new Date())).format(
            "MM/DD/YYYY"
          ),
          total_amount: 0,
          paymentTypeCash: "1",
          invoice_id: "",
          aPatient: {},
          lstPatientPaymentDetails: [],
          refund_amount: 0,
          isRefund: false,
          ipdServiceList: [],
        };
      } else {
        //this.isDueExist = "";
        this.patientPayment.refund_amount = 0;
        this.patientPayment.isRefund = false;
        this.patientPayment.invoice_id = data[0].invoice_id;
        this.patientPayment.admission_id = data[0].admission_id;
        //this.patientPayment.discharge_summary = data[0].discharge_summary;
        if (data[0].discharge_summary != null) {
          this.patientPayment.isReadyForSettlement = true;
        }
        if (data[0].discharge_summary == null) {
          this.patientPayment.isReadyForSettlement = false;
        }
        this.patientPayment.reffered_by_id = data[0].reffered_by_id;
        this.patientPayment.aPatient.patient_id = data[0].patient_id;
        this.patientPayment.aPatient.first_name = data[0].first_name;
        this.patientPayment.aPatient.contact_no = data[0].contact_no;

        this.patientPayment.bill_amount = data[0].total_amount;
        this.patientPayment.total_amount = data[0].due_amount;
        if (data[0].due_amount < 0) {
          this.patientPayment.cash_paid_amount = 0;
          this.patientPayment.ptypaid_amount = 0;
          this.patientPayment.advance_amount = data[0].total_amount + Math.abs(data[0].due_amount);
        } else {
          this.patientPayment.cash_paid_amount = data[0].due_amount;
          this.patientPayment.ptypaid_amount = data[0].due_amount;
        }

        this.patientPayment.cdadmission_date = moment(
          String(data[0].admission_date)
        ).format("YYYY-MM-DD");

        //
        this.patientPayment.ipdServiceList = JSON.parse(obj.serviceList);
      }
    },
    findIPDPaymentInfo() {
      this.reset();
      this.patientPaymentService
        .getIPDPableByadmission_no(this.admission_no)
        .then((data) => this.getFormatted(data.data));
    },
    paymentTypeIsExist() {
      var aPaymentType = this.patientPayment.lstPatientPaymentDetails.filter(
        function (el) {
          return (
            el.payment_type_id ===
            this.patientPayment.ddlPaymentType.payment_type_id
          );
        },
        this
      );

      if (aPaymentType.length) {
        //this.isExist = "This Payment Type already added";
        this.$toast.add({
          severity: "error",
          summary: "Error Message",
          detail: "This Payment Type already added",
          life: 10000,
        });
        this.isPayment = true;
        return;
      } else {
        //this.isExist = "";
        this.isPayment = false;
      }
    },
    addPaymentType() {
      this.submitted = true;
      var aPaymentType = this.patientPayment.lstPatientPaymentDetails.filter(
        function (el) {
          return (
            el.payment_type_id ===
            this.patientPayment.ddlPaymentType.payment_type_id
          );
        },
        this
      );

      if (aPaymentType.length) {
        //this.isExist = "This Payment Type already added";
        this.$toast.add({
          severity: "error",
          summary: "Error Message",
          detail: "This Payment Type already added",
          life: 10000,
        });
        //this.isPayment = true;
        return;
      } else {
        if (!this.patientPayment.aPatient.first_name) {
          return;
        } else if (!this.patientPayment.cdpayment_date) {
          this.$toast.add({
            severity: "error",
            summary: "Error Message",
            detail: "Payment Date is required",
            life: 10000,
          });
          return;
        } else if (!this.patientPayment.ddlPaymentType) {
          //this.isExist = "Payment Type is required.";
          this.$toast.add({
            severity: "error",
            summary: "Error Message",
            detail: "Payment Type is required",
            life: 10000,
          });
          return;
        } else if (
          this.patientPayment.ptypaid_amount <= 0 ||
          !this.patientPayment.ptypaid_amount
        ) {
          //this.isPaid = "Non-Cash Paid Amt is required";
          this.$toast.add({
            severity: "error",
            summary: "Error Message",
            detail: "Non-Cash Paid Amt is required",
            life: 10000,
          });
          return;
        } else if (this.patientPayment.refund_amount > 0) {
          //this.isPaid = "You have Refund Amt";
          this.$toast.add({
            severity: "error",
            summary: "Error Message",
            detail: "You have Refund Amt",
            life: 10000,
          });
          return;
        } else if (this.patientPayment.isReadyForSettlement && this.patientPayment.total_amount <= 0) {
          //this.isPaid = "You have Refund Amt";
          this.$toast.add({
            severity: "error",
            summary: "Error Message",
            detail: "You have Refund Amt",
            life: 10000,
          });
          return;
        }
        // else if (
        //   this.patientPayment.cash_paid_amount <= 0 ||
        //   this.patientPayment.cash_paid_amount <
        //   this.patientPayment.ptypaid_amount
        // ) {
        //   this.isPaid = "Exceed amount!";
        //   return;
        // } 
        else {
          //this.isExist = "";
          //this.isPaid = "";

          var obj = {
            payment_type_id: this.patientPayment.ddlPaymentType.payment_type_id,
            _payment_type_name:
              this.patientPayment.ddlPaymentType.payment_type_name,

            paid_amount: parseInt(this.patientPayment.ptypaid_amount),
          };

          this.patientPayment.lstPatientPaymentDetails.push(obj);

          // this.totalPtyAmount =
          //   this.totalPtyAmount + parseInt(this.patientPayment.ptypaid_amount);

          // this.patientPayment.cash_paid_amount =
          //   this.patientPayment.total_amount - this.totalPtyAmount;
          this.gridSum();
        }
      }

      //this.addList = true;
      this.button = true;
    },

    deleteFromList(data) {
      this.patientPayment.lstPatientPaymentDetails =
        this.patientPayment.lstPatientPaymentDetails.filter(function (el) {
          return el.payment_type_id != data.payment_type_id;
        }, this);

      // this.patientPayment.cash_paid_amount =
      //   this.patientPayment.cash_paid_amount + parseInt(data.paid_amount);

      if (!this.patientPayment.lstPatientPaymentDetails.length) {
        //this.addList = false;
        this.button = false;
      }
      this.gridSum();
    },
    gridSum() {
      this.patientPayment.total_amount_inf = 0;
      this.patientPayment.lstPatientPaymentDetails.forEach((element) => {
        this.patientPayment.total_amount_inf += element.paid_amount;
      });
      this.patientPayment.cash_paid_amount = 0;
      //this.patientPayment.total_amount - this.patientPayment.total_amount_inf - this.patientPayment.discount_amount;
      if (this.patientPayment.cash_paid_amount < 0) {
        this.patientPayment.cash_paid_amount = 0;
      }
      this.dueCalculate();
    },
    dueCalculate() {
      // Line Discount Start
      var withOutLineDiscountTotalServiceCost = 0;
      var withLineDiscountTotalServiceCost = 0;
      this.patientPayment.ipdServiceList.forEach(
        (element) => {
          withOutLineDiscountTotalServiceCost = withOutLineDiscountTotalServiceCost + element.charge;
          withLineDiscountTotalServiceCost = withLineDiscountTotalServiceCost + element.total_amount;
        }
      );
      // Line Discount End

      this.patientPayment.due_amount =
        this.patientPayment.total_amount -
        this.patientPayment.cash_paid_amount -
        this.patientPayment.total_amount_inf -
        this.patientPayment.discount_amount - withOutLineDiscountTotalServiceCost + withLineDiscountTotalServiceCost;

      if (this.patientPayment.due_amount < 0) {
        this.patientPayment.refund_amount = Math.abs(this.patientPayment.due_amount);
      } else {
        this.patientPayment.refund_amount = 0;
      }
      // if (
      //   this.patientPayment.total_amount < this.patientPayment.cash_paid_amount
      // ) {
      //   this.isCashPaid = "Exceed amount!";
      // } else {
      //   this.isCashPaid = "";
      // }
    },
    calculateChangeAmount() {
      if (this.patientPayment.cash_amount.length) {
        this.patientPayment.change_amount =
          this.patientPayment.cash_amount -
          this.patientPayment.cash_paid_amount;
      } else {
        this.patientPayment.change_amount = 0;
      }
    },
    getDiscountPercent() {
      this.patientPayment.discount_percent = Math.ceil(
        (this.patientPayment.discount_amount /
          this.patientPayment.bill_amount) *
        100);

      this.gridSum();
    },
    getDiscountAmount() {
      this.patientPayment.discount_amount =
        (this.patientPayment.bill_amount *
          this.patientPayment.discount_percent) /
        100;

      this.gridSum();
    },
    getRefundAmt() {
      this.gridSum();

      if (!this.patientPayment.isSettlement && this.patientPayment.total_amount < 0) {
        //this.patientPayment.refund_amount = Math.abs(this.patientPayment.total_amount);
        this.patientPayment.isRefund = true;
      } else {
        this.patientPayment.refund_amount = 0;
        this.patientPayment.isRefund = false;
      }
    },
    async add() {
      this.submitted = true;
      try {
        if (!this.patientPayment.aPatient.first_name) {
          return;
        } else if (!this.patientPayment.cdpayment_date) {
          this.$toast.add({
            severity: "error",
            summary: "Error Message",
            detail: "Payment Date is required",
            life: 10000,
          });
          return;
        } else if (!this.patientPayment.invoice_id) {
          return;
        } else if (this.patientPayment.cash_paid_amount <= 0 && !this.patientPayment.isReadyForSettlement) {
          this.$toast.add({
            severity: "error",
            summary: "Error",
            detail: "Enter amount!",
            life: 10000,
          });
          return;
        } else if (this.patientPayment.isSettlement && this.patientPayment.due_amount > 0) {
          this.$toast.add({
            severity: "error",
            summary: "Error",
            detail: "Cannot settlement with due amount!",
            life: 10000,
          });
          return;
        } else if (this.isCashPaid) {
          return;
        } else if (this.patientPayment.isRefund && this.patientPayment.refund_amount > 0 && this.patientPayment.cash_paid_amount > 0) {
          this.$toast.add({
            severity: "error",
            summary: "Error",
            detail: "You have Refund Amt",
            life: 10000,
          });
          return;
        } else if (this.patientPayment.isReadyForSettlement && this.patientPayment.total_amount <= 0 && this.patientPayment.cash_paid_amount > 0) {
          this.$toast.add({
            severity: "error",
            summary: "Error",
            detail: "You have Refund Amt",
            life: 10000,
          });
          return;
        } else {
          this.patientPayment.payment_date = moment(
            String(this.patientPayment.cdpayment_date)
          ).format("YYYY-MM-DD");

          if (this.patientPayment.cash_paid_amount > 0 && !this.isCash) {
            this.patientPayment.lstPatientPaymentDetails.push({
              payment_type_id: this.patientPayment.paymentTypeCash,

              paid_amount: this.patientPayment.cash_paid_amount,
            });

            this.isCash = true;
          }
          this.patientPayment.paid_amount =
            parseInt(this.patientPayment.cash_paid_amount) +
            parseInt(this.patientPayment.total_amount_inf);

          let result = await this.patientPaymentService.addIPD(
            this.patientPayment, this
          );
          if (result.status === 200) {
            this.$toast.add({
              severity: "success",
              summary: "Success",
              detail: "Saved Successfully",
              life: 10000,
            });
            if (this.patientPayment.isSettlement) {
              this.printDischargeBillSummary(result.data)
            } else {
              this.printAdmissionPayment(result.data);
            }

            var admission_no_for_payment_from_admissionlist = localStorage.getItem('admission_no_for_payment_from_admissionlist');
            if (admission_no_for_payment_from_admissionlist) {
              localStorage.removeItem('admission_no_for_payment_from_admissionlist');
              this.$router.push("/patient-admission-list");
            }

            var admission_id_for_payment = localStorage.getItem('admission_id_for_payment');
            if (admission_id_for_payment) {
              localStorage.removeItem('admission_id_for_payment');
              this.$router.push("/patient-admission-form");
            }
            this.reset();
          } else if (result.status === 401) {
            this.$toast.add({
              severity: "error",
              summary: "Error",
              detail: result.data,
              life: 10000,
            });
          } else {
            this.$toast.add({
              severity: "error",
              summary: "Failed",
              detail: result.res,
              life: 10000,
            });
            this.reset();
          }
        }
      } catch (error) {
        this.$toast.add({
          severity: "error",
          summary: "Failed",
          detail: error,
          life: 10000,
        });
        this.reset();
      }
    },

    // line discount
    calculatePerInvestigationDiscountAmount(data) {
      this.patientPayment.ipdServiceList.forEach(
        (element) => {
          if (element.type == data.type && element.admission_bed_service_package_details_id == data.admission_bed_service_package_details_id) {
            element.discount_amount = (element.charge * element.discount_percent) / 100;
            element.total_amount = element.charge - element.discount_amount;
          }
        }
      );
      this.gridSum();
    },
    calculatePerInvestigationDiscountPercent(data) {
      this.patientPayment.ipdServiceList.forEach(
        (element) => {
          if (element.type == data.type && element.admission_bed_service_package_details_id == data.admission_bed_service_package_details_id) {
            element.discount_percent = (element.discount_amount / element.charge) * 100;
            //var discount_amount = (element.charge * element.discount_percent) / 100;
            element.total_amount = element.charge - element.discount_amount;
          }
        }
      );
      this.gridSum();
    },
  },
};
