import AdmissionService from "../../service/front-desk/AdmissionService";

import moment from "moment";
import Validation from "../../scripts/validation";
import { FilterMatchMode, FilterOperator } from "primevue/api";
// Prescription
import AppointmentPrescriptionService from "../../service/doctor-panel/AppointmentPrescriptionService";
import AdmissionPrescriptionService from "../../service/prescription/AdmissionPrescriptionService";
// Prescription Treatment
import MedicineTypeSetupService from "../../service/setup/MedicineTypeSetupService";
import MedicineSetupService from "../../service/setup/MedicineSetupService";
import TreatmentRulesSetupService from "../../service/setup/TreatmentRulesSetupService";
import TreatmentDurationSetupService from "../../service/setup/TreatmentDurationSetupService";
import TreatmentDoseSetupService from "../../service/setup/TreatmentDoseSetupService";
// History
import PatientProfileService from "../../service/profiles/PatientProfileService";
//import WordCabinSetupService from "../../service/setup/WordCabinSetupService";
import ComplaintSetupService from "../../service/setup/ComplaintSetupService";
import FoodService from "../../service/setup/FoodService";
import OnExaminationSetupService from "../../service/setup/OnExaminationSetupService";
import DietChartTemplateSetupService from "../../service/setup/DietChartTemplateSetupService";
import DietChartSetupService from "../../service/setup/DietChartSetupService";
import DiagnosisSetupService from "../../service/setup/DiagnosisSetupService";
import CommonService from "../../service/CommonService";
import PrescriptionReportService from "../../service/report/PrescriptionReportService";
// Discharge
import IPDService from "../../service/ipd/IPDService";

export default {
  data() {
    return {
      dataList: true,
      isDoctor: false,
      admittedPatientList: [],
      admittedPatient: {
        cdfromdate: moment(String(new Date())).format("MM/DD/YYYY"),
        // cdfromdate: moment(
        //   String(new Date(new Date().setDate(new Date().getDate() - 60)))
        // ).format("MM/DD/YYYY"),
        cdtodate: moment(String(new Date())).format("MM/DD/YYYY"),
        // cdtodate: moment(
        //   String(new Date(new Date().setDate(new Date().getDate() + 7)))
        // ).format("MM/DD/YYYY"),
      },
      //

      submitted: false,
      filters: {
        global: { value: null, matchMode: FilterMatchMode.CONTAINS },
        name: { value: null, matchMode: FilterMatchMode.STARTS_WITH },
      },
      initFilters() {
        this.filters = {
          global: { value: null, matchMode: FilterMatchMode.CONTAINS },
          name: {
            operator: FilterOperator.AND,
            constraints: [
              { value: null, matchMode: FilterMatchMode.STARTS_WITH },
            ],
          },
        };
      },

      // Discharge
      isDischarge: false,
      dischargeDialog: false,
      dischargeObj: {
        aAdmissionBedDetails: {},
      },
      // Prescription
      admittedPatientInfo: {},
      prescriptionDataList: [],
      tempData: [],
      headerName: "Sections",

      admissionPrescriptionInfo: {
        cdprescription_date: moment(String(new Date())).format("MM/DD/YYYY"),
        cdfolowup_date: moment(
          String(new Date(new Date().setDate(new Date().getDate() + 7)))
        ).format("MM/DD/YYYY"),

        allAdvice: false,

        lstAdmissionPrescriptionInvestigation: [],
        lstAdmissionPrescriptionTreatment: [],
        lstAdmissionPrescriptionAdvice: [],
      },

      // Prescription Treatment
      treatmentAddDialog: false,
      treatmentDialog: false,
      treatmentSetup: {},
      medicineTypeList: [],
      medicineList: [],
      treatmentRulesList: [],
      treatmentDurationList: [],
      treatmentDoseList: [],

      // History
      isHistory: false,
      active: 0,
      expandedRows: [],
      isFemale: false,

      chiefComplainDialog: false,
      chiefComplain: {},
      chiefComplainList: [],
      compliantList: [],

      foodDialog: false,
      foodSetup: {},
      allergyDetailsDialog: false,
      allergyDetails: {},
      allergyDetailsList: [],
      foodList: [],
      allergyTypeList: [],

      onExaminationDialog: false,
      onExamination: {},
      patientOnExaminationList: [],
      onExaminationList: [],

      iOChartDialog: false,
      iOChart: {},
      iOChartList: [],

      diabetesChartDialog: false,
      diabetesChart: {
        aAdmissionServiceDetails: {}
      },
      diabetesChartList: [],
      diabetesMealList: [],

      dietChartDialog: false,
      dietChart: {},
      dietChartList: [],
      dietChartTemplateList: [],
      dietChartTempList: [],

      currentMedicationsDialog: false,
      currentMedications: {},
      currentMedicationsList: [],

      pastMedicalHistoryDialog: false,
      pastMedicalHistory: {},
      pastMedicalHistoryList: [],
      diagnosisList: [],

      patientLatestHistory: {
        lstPatientOnExamination: [],
        lstPatientAllergyDetails: [],
        lstPatientDiabetesChart: [],
        lstPatientDietChart: [],
      },
      patientHistory: {
        lstPersonalHistory: [],
        lstFamilyHistory: [],
        lstSystemsReview: [],
        lstSubstanceUse: [],
        lstReproductiveHistory: [],
      },
    };
  },
  created() {
    // past date-year disabled
    let today = new Date();
    let month = today.getMonth();
    let year = today.getFullYear();
    let prevMonth = (month === 0) ? 11 : month - 0;
    let prevYear = (prevMonth === 11) ? year - 0 : year;
    this.minDate = new Date();
    this.minDate.setMonth(prevMonth);
    this.minDate.setFullYear(prevYear);

    //this.wordCabinSetupService = new WordCabinSetupService();
    this.admissionService = new AdmissionService();
    // Prescription
    this.admissionPrescriptionService = new AdmissionPrescriptionService();
    this.appointmentPrescriptionService = new AppointmentPrescriptionService();

    // Prescription Treatment
    this.medicineTypeSetupService = new MedicineTypeSetupService();
    this.medicineSetupService = new MedicineSetupService();
    this.treatmentRulesSetupService = new TreatmentRulesSetupService();
    this.treatmentDurationSetupService = new TreatmentDurationSetupService();
    this.treatmentDoseSetupService = new TreatmentDoseSetupService();

    // History
    this.patientProfileService = new PatientProfileService();

    this.complaintSetupService = new ComplaintSetupService();
    this.foodService = new FoodService();
    this.onExaminationSetupService = new OnExaminationSetupService();
    this.dietChartTemplateSetupService = new DietChartTemplateSetupService();
    this.dietChartSetupService = new DietChartSetupService();

    this.diagnosisSetupService = new DiagnosisSetupService();
    this.commonService = new CommonService();
    this.validation = new Validation();
    this.prescriptionReportService = new PrescriptionReportService();

    // Discharge
    this.iPDService = new IPDService();

    // this.wordCabinSetupService
    //     .getAll()
    //     .then((data) => (this.wordCabinList = data.data));

    let that = this;
    document.onkeyup = function (e) {
      if (e.ctrlKey && e.altKey && e.which == 73) {
        that.getInvestigation();
      } else if (e.ctrlKey && e.altKey && e.which == 84) {
        that.getTreatment();
      } else if (e.ctrlKey && e.altKey && e.which == 65) {
        that.getAdvice();
      }
      // else if (e.ctrlKey && e.altKey && e.shiftKey) {

      // }
    };

    this.medicineTypeSetupService.getActiveCombo().then((data) => (this.medicineTypeList = data.data));

    this.userInfo = JSON.parse(localStorage.getItem("userInfo"));
    this.show();

    if (this.userInfo.doctor_id) {
      this.appointmentPrescriptionService
        .getPrescriptionDataByDoctorId(this.userInfo.doctor_id)
        .then((data) => (this.prescriptionDataList = data.data));

      this.doctor_id = this.userInfo.doctor_id;
      this.admissionPrescriptionInfo.doctor_id = this.doctor_id;

      this.isDoctor = true;
    } else if (!this.userInfo.doctor_id) {
      this.isDoctor = false;
    }
  },
  methods: {
    show() {
      this.submitted = true;
      if (!this.admittedPatient.cdfromdate) {
        return;
      } else if (!this.admittedPatient.cdtodate) {
        return;
      } else {
        this.admittedPatient.fromdate = moment(
          String(this.admittedPatient.cdfromdate)
        ).format("YYYY-MM-DD");
        this.admittedPatient.todate = moment(
          String(this.admittedPatient.cdtodate)
        ).format("YYYY-MM-DD");
        this.userInfo = JSON.parse(localStorage.getItem("userInfo"));
        this.admissionService
          .admittedPatientListForTreatmentByDoctorId(this.admittedPatient.fromdate, this.admittedPatient.todate, this.userInfo.doctor_id)
          .then((data) => (this.admittedPatientList = data.data));

        this.submitted = false;
      }
      this.dataList = true;
    },

    prescription(data) {
      this.admittedPatientInfo.admission_id = data.admission_id;
      this.admittedPatientInfo.admission_no = data.admission_no;
      localStorage.setItem('admissionIdForDiabetesService', data.admission_id);
      this.admittedPatientInfo.patient_id = data.patient_id;
      localStorage.setItem('patientIdForTreatment', data.patient_id);
      this.admittedPatientInfo.registration_no = data.registration_no;
      this.admittedPatientInfo.patient_name = data.patient_name;
      this.admittedPatientInfo.gender = data.gender;
      this.admittedPatientInfo.age_year = data.age_year;
      this.admittedPatientInfo.age_month = data.age_month;
      this.admittedPatientInfo.age_day = data.age_day;

      this.dataList = false;
      this.getComplainsByPatientId(data.patient_id);
      this.getLatestHistoryByPatientIdForPrescription();
      // Use for Dialog //
      this.complaintSetupService
        .getActiveCombo()
        .then((data) => (this.compliantList = data.data));

      this.foodService
        .getActiveCombo()
        .then((data) => (this.foodList = data.data));

      this.onExaminationSetupService
        .getActiveCombo()
        .then((data) => (this.onExaminationList = data.data));

      this.diagnosisSetupService
        .getAll()
        .then((data) => (this.diagnosisList = data.data));

      this.commonService
        .GetAllAllergyType()
        .then((data) => (this.allergyTypeList = data.data));

      this.commonService
        .GetAllDiabetesMeal()
        .then((data) => (this.diabetesMealList = data.data));

      this.dietChartTemplateSetupService
        .getActiveCombo()
        .then((data) => (this.dietChartTemplateList = data.data));
      // Use for Dialog //

      // Previous Prescription Data
      this.admissionPrescriptionService
        .getPreviousPrescriptionDataAdmissionId(data.admission_id)
        .then((data) => this.previousPrescriptionDataFormatted(data.data));

      // Prescription Treatment
      this.medicineSetupService
        .getActiveCombo()
        .then((data) => (this.medicineList = data.data));

      this.treatmentRulesSetupService
        .getActiveCombo()
        .then((data) => (this.treatmentRulesList = data.data));

      this.treatmentDurationSetupService
        .getActiveCombo()
        .then((data) => (this.treatmentDurationList = data.data));

      this.treatmentDoseSetupService
        .getActiveCombo()
        .then((data) => (this.treatmentDoseList = data.data));
    },
    previousPrescriptionDataFormatted(data) {
      this.admissionPrescriptionInfo.cdprescription_date = moment(String(new Date())).format("MM/DD/YYYY");
      this.admissionPrescriptionInfo.cdfolowup_date = moment(
        String(new Date(new Date().setDate(new Date().getDate() + 7)))
      ).format("MM/DD/YYYY");
      this.admissionPrescriptionInfo.lstAdmissionPrescriptionTreatment = data.lstAdmissionPrescriptionTreatment;
      this.admissionPrescriptionInfo.lstAdmissionPrescriptionAdvice = data.lstAdmissionPrescriptionAdvice;
      this.admissionPrescriptionInfo.lstAdmissionPrescriptionInvestigation = data.lstAdmissionPrescriptionInvestigation;
    },
    getLatestHistoryByPatientIdForPrescription() {
      var patientId = JSON.parse(localStorage.getItem("patientIdForTreatment")).toString();

      this.patientProfileService.getLatestHistoryByPatientIdForPrescription(patientId).then((data) => (this.patientLatestHistory = data.data));
    },

    // Get Prescription Data
    getInvestigation() {
      this.headerName = "Investigation";
      this.filters['global'].value = "";
      this.isAdvice = false;
      this.tempData = [];
      this.prescriptionDataList.lstInvestigation.forEach((element) => {
        this.tempData.push({
          name: element.investingation_name,
          id: element.investigation_id,
        });
      });
    },
    getTreatment() {
      this.treatmentAddDialog = true;
      // this.headerName = "Medicine";
      // this.filters['global'].value = "";
      // this.isAdvice = false;
      // this.tempData = [];
      // this.prescriptionDataList.lstTreatment.forEach((element) => {
      //   this.tempData.push({
      //     _medicine_type_name: element._medicine_type_name,
      //     _medicine_name: element._medicine_name,
      //     name: element._medicine_type_name + " " + element._medicine_name + " " + element._treatment_dose_name + " " + element._treatment_rule_name + " " + element._treatment_duration_name,
      //     id: element.medicine_id,
      //     _treatment_dose_name: element._treatment_dose_name,
      //     treatment_dose_id: element.treatment_dose_id,
      //     _treatment_rule_name: element._treatment_rule_name,
      //     treatment_rules_id: element.treatment_rules_id,
      //     _treatment_duration_name: element._treatment_duration_name,
      //     treatment_duration_id: element.treatment_duration_id,
      //     description: element.description,
      //   });
      // });
    },
    saveTreatment() {
      this.submitted = true;
      if (!this.treatmentSetup.ddlmedicine_type) {
        return;
      } else if (!this.treatmentSetup.ddlmedicine) {
        return;
      } else if (!this.treatmentSetup.ddltreatment_rules) {
        return;
      } else if (!this.treatmentSetup.ddltreatment_duration) {
        return;
      } else if (!this.treatmentSetup.ddltreatment_dose) {
        return;
      } else {
        var aMedicine =
          this.admissionPrescriptionInfo.lstAdmissionPrescriptionTreatment.filter(
            function (el) {
              return el.medicine_id === this.treatmentSetup.ddlmedicine.value;
            },
            this
          );

        if (!aMedicine.length) {
          this.admissionPrescriptionInfo.lstAdmissionPrescriptionTreatment.push({
            _medicine_type_name: this.treatmentSetup.ddlmedicine_type.text,
            name: this.treatmentSetup.ddlmedicine.text,
            medicine_id: this.treatmentSetup.ddlmedicine.value,
            _treatment_dose_name: this.treatmentSetup.ddltreatment_dose.text,
            treatment_dose_id: this.treatmentSetup.ddltreatment_dose.value,
            _treatment_rule_name: this.treatmentSetup.ddltreatment_rules.text,
            treatment_rules_id: this.treatmentSetup.ddltreatment_rules.value,
            _treatment_duration_name: this.treatmentSetup.ddltreatment_duration.text,
            treatment_duration_id: this.treatmentSetup.ddltreatment_duration.value,
            description: "",
          });
        }
        this.hideDialog();
      }
    },
    getAdvice() {
      this.headerName = "Advice";
      this.filters['global'].value = "";
      this.isAdvice = true;
      this.tempData = [];
      this.prescriptionDataList.lstAdvices.forEach((element) => {
        this.tempData.push({
          name: element.advice_name,
          id: element.advice_id,
        });
      });
    },
    // Add all advice checkbox
    addAllAdvice() {
      if (!this.admissionPrescriptionInfo.allAdvice) {
        this.tempData.forEach((element) => {
          var aAdvice =
            this.admissionPrescriptionInfo.lstAdmissionPrescriptionAdvice.filter(
              function (el) {
                return el.advice_id === element.id;
              },
              this
            );

          if (!aAdvice.length) {
            this.admissionPrescriptionInfo.lstAdmissionPrescriptionAdvice.push({
              name: element.name,
              advice_id: element.id,
            });
          }
        });
      }
    },
    // Add value 
    getValue(data) {
      if (this.headerName == "Investigation") {
        var aInvestigation =
          this.admissionPrescriptionInfo.lstAdmissionPrescriptionInvestigation.filter(
            function (el) {
              return el.investigation_id === data.id;
            },
            this
          );

        if (!aInvestigation.length) {
          this.admissionPrescriptionInfo.lstAdmissionPrescriptionInvestigation.push(
            {
              name: data.name,
              investigation_id: data.id,
            }
          );
        }
      } else if (this.headerName == "Medicine") {
        var aMedicine =
          this.admissionPrescriptionInfo.lstAdmissionPrescriptionTreatment.filter(
            function (el) {
              return el.medicine_id === data.id;
            },
            this
          );

        if (!aMedicine.length) {
          this.admissionPrescriptionInfo.lstAdmissionPrescriptionTreatment.push({
            _medicine_type_name: data._medicine_type_name,
            name: data._medicine_name,
            medicine_id: data.id,
            _treatment_dose_name: data._treatment_dose_name,
            treatment_dose_id: data.treatment_dose_id,
            _treatment_rule_name: data._treatment_rule_name,
            treatment_rules_id: data.treatment_rules_id,
            _treatment_duration_name: data._treatment_duration_name,
            treatment_duration_id: data.treatment_duration_id,
            description: data.description,
          });
        }
      } else if (this.headerName == "Advice") {
        var aAdvice =
          this.admissionPrescriptionInfo.lstAdmissionPrescriptionAdvice.filter(
            function (el) {
              return el.advice_id === data.id;
            },
            this
          );

        if (!aAdvice.length) {
          this.admissionPrescriptionInfo.lstAdmissionPrescriptionAdvice.push({
            name: data.name,
            advice_id: data.id,
          });
        }
      }
    },

    hideDialog() {
      this.treatmentAddDialog = false;
      this.treatmentDialog = false;

      var patientId = JSON.parse(localStorage.getItem("patientIdForTreatment")).toString();

      this.chiefComplainDialog = false;
      this.chiefComplain = {
        patient_id: patientId
      };

      this.foodDialog = false;
      this.foodSetup = {};
      this.allergyDetailsDialog = false;
      this.allergyDetails = {
        patient_id: patientId
      };

      this.onExaminationDialog = false;
      // this.onExamination = {
      //   patient_id: patientId
      // };

      this.iOChartDialog = false;
      this.iOChart = {
        patient_id: patientId
      };

      this.diabetesChartDialog = false;
      this.diabetesChart = {
        aAdmissionServiceDetails: {},
        patient_id: patientId
      };

      this.dietChartDialog = false;
      this.dietChart = {
        patient_id: patientId
      };

      this.currentMedicationsDialog = false;
      this.currentMedications = {
        patient_id: patientId
      };

      this.pastMedicalHistoryDialog = false;
      this.pastMedicalHistory = {
        patient_id: patientId
      };

      this.submitted = false;

      // Discharge
      this.dischargeDialog = false;
      this.dischargeObj = {
        aAdmissionBedDetails: {},
      };
    },
    filterMedicineTypeWise() {
      if (this.treatmentSetup.ddlmedicine_type) {
        this.treatmentSetup.ddlmedicine_type.text;
        return this.medicineList.filter(function (el) {
          return el.refid === this.treatmentSetup.ddlmedicine_type.value;
        }, this);
      }
    },
    editTreatment(data, index) {
      this.treatmentSetup.medicineName = data.name;
      this.treatmentSetup.ddltreatment_rules = {
        text: data._treatment_rule_name,
        value: data.treatment_rules_id,
        refid: null,
      };
      this.treatmentSetup.ddltreatment_duration = {
        text: data._treatment_duration_name,
        value: data.treatment_duration_id,
        refid: null,
      };
      this.treatmentSetup.ddltreatment_dose = {
        text: data._treatment_dose_name,
        value: data.treatment_dose_id,
        refid: null,
      };
      this.treatmentDialog = true;
      this.treatmentIndex = index
    },
    treatmentEditSave() {
      this.submitted = true;
      if (!this.treatmentSetup.ddltreatment_rules) {
        return;
      } else if (!this.treatmentSetup.ddltreatment_duration) {
        return;
      } else if (!this.treatmentSetup.ddltreatment_dose) {
        return;
      } else {
        //this.admissionPrescriptionInfo.lstAdmissionPrescriptionTreatment[this.treatmentIndex].name = this.treatmentSetup.ddlmedicine.text;
        //this.admissionPrescriptionInfo.lstAdmissionPrescriptionTreatment[this.treatmentIndex].medicine_id = this.treatmentSetup.ddlmedicine.value;
        this.admissionPrescriptionInfo.lstAdmissionPrescriptionTreatment[this.treatmentIndex]._treatment_rule_name = this.treatmentSetup.ddltreatment_rules.text;
        this.admissionPrescriptionInfo.lstAdmissionPrescriptionTreatment[this.treatmentIndex].treatment_rules_id = this.treatmentSetup.ddltreatment_rules.value;
        this.admissionPrescriptionInfo.lstAdmissionPrescriptionTreatment[this.treatmentIndex]._treatment_duration_name = this.treatmentSetup.ddltreatment_duration.text;
        this.admissionPrescriptionInfo.lstAdmissionPrescriptionTreatment[this.treatmentIndex].treatment_duration_id = this.treatmentSetup.ddltreatment_duration.value;
        this.admissionPrescriptionInfo.lstAdmissionPrescriptionTreatment[this.treatmentIndex]._treatment_dose_name = this.treatmentSetup.ddltreatment_dose.text;
        this.admissionPrescriptionInfo.lstAdmissionPrescriptionTreatment[this.treatmentIndex].treatment_dose_id = this.treatmentSetup.ddltreatment_dose.value;

        this.treatmentDialog = false;
        this.submitted = false;
      }
    },
    deleteItem(lst, data) {
      lst.splice(
        lst.findIndex((i) => i === data),
        1
      );
    },
    async add() {
      try {
        if (
          !this.admissionPrescriptionInfo.lstAdmissionPrescriptionAdvice
            .length
        ) {
          this.$toast.add({
            severity: "error",
            summary: "Error",
            detail: "Please add Patient Advice",
            life: 10000,
          });
          return;
        } else {
          this.admissionPrescriptionInfo.admission_id =
            this.admittedPatientInfo.admission_id;

          this.admissionPrescriptionInfo.prescription_date = moment(
            String(this.admissionPrescriptionInfo.cdprescription_date)
          ).format("YYYY-MM-DD");

          if (this.admissionPrescriptionInfo.cdfolowup_date) {
            this.admissionPrescriptionInfo.folowup_date = moment(
              String(this.admissionPrescriptionInfo.cdfolowup_date)
            ).format("YYYY-MM-DD");
          }

          let result = await this.admissionPrescriptionService.add(
            this.admissionPrescriptionInfo
          );
          if (result.status === 200) {
            this.downloadPrescription(result.data)
            this.$toast.add({
              severity: "success",
              summary: "Success",
              detail: "Prescribed Successfully",
              life: 10000,
            });
            this.newPrescription();
            this.reset();
            this.show();
          } else {
            this.$toast.add({
              severity: "error",
              summary: "Failed",
              detail: result.res,
              life: 10000,
            });
          }
        }
      } catch (error) {
        this.$toast.add({
          severity: "error",
          summary: "Failed",
          detail: error,
          life: 10000,
        });
      }
    },
    downloadPrescription(admission_prescription_id) {
      this.prescriptionReportService.GetIPDPescription(admission_prescription_id, this).then((data) => (this.commonService.Downloadfile(data.data)));
    },
    newPrescription() {
      this.headerName = "Sections";
      this.filters['global'].value = "";
      this.tempData = [];

      this.admissionPrescriptionInfo = {
        cdprescription_date: moment(String(new Date())).format("MM/DD/YYYY"),
        cdfolowup_date: moment(
          String(new Date(new Date().setDate(new Date().getDate() + 7)))
        ).format("MM/DD/YYYY"),

        allAdvice: false,

        lstAdmissionPrescriptionInvestigation: [],
        lstAdmissionPrescriptionTreatment: [],
        lstAdmissionPrescriptionAdvice: [],
      },

        this.submitted = false;
    },
    reset() {
      this.admittedPatient = {
        cdfromdate: moment(String(new Date())).format("MM/DD/YYYY"),
        // cdfromdate: moment(
        //   String(new Date(new Date().setDate(new Date().getDate() - 60)))
        // ).format("MM/DD/YYYY"),
        cdtodate: moment(String(new Date())).format("MM/DD/YYYY"),
        // cdtodate: moment(
        //   String(new Date(new Date().setDate(new Date().getDate() + 7)))
        // ).format("MM/DD/YYYY"),
      },
        //
        // this.dataList = true;
        // this.submitted = false;
        this.isDischarge = false;
      this.show();
    },
    formatDate(value) {
      return moment(String(value)).format("MM/DD/YYYY");
    },
    formatDateTime(value) {
      return moment(String(value)).format("MM/DD/YYYY h:mm:ss a");
    },

    // History
    history() {
      var data = this.admittedPatientInfo;
      if (data.gender == "Female") {
        this.isFemale = true;
      } else {
        this.isFemale = false;
      }

      var patientId = JSON.parse(localStorage.getItem("patientIdForTreatment"));
      if (patientId) {
        localStorage.removeItem('patientIdForTreatment');
        localStorage.setItem('patientIdForTreatment', data.patient_id);
      } else {
        localStorage.setItem('patientIdForTreatment', data.patient_id);
      }

      // Collect history patient wise //
      this.getComplainsByPatientId(data.patient_id);

      this.getAllergyDetailsByPatientId(data.patient_id);

      this.getOnExaminationByPatientId(data.patient_id);

      this.getIOChartByPatientId(data.patient_id);

      this.getDiabetesChartByPatientId(data.patient_id);

      this.getDietChartByPatientId(data.patient_id);

