import AdmissionService from "../../service/front-desk/AdmissionService";
import PatientRegistrationService from "../../service/front-desk/PatientRegistrationService";
import CommonService from "../../service/CommonService";
import ConsultancyDepartmentSetupService from "../../service/setup/ConsultancyDepartmentSetupService";
import DoctorSetupService from "../../service/setup/DoctorSetupService";
import BuildingSetupService from "../../service/setup/BuildingSetupService";
import WordCabinCategorySetupService from "../../service/setup/WordCabinCategorySetupService";
import WordCabinTypeSetupService from "../../service/setup/WordCabinTypeSetupService";
import WordCabinSetupService from "../../service/setup/WordCabinSetupService";
import BedSetupService from "../../service/setup/BedSetupService";
import BedStatusService from "../../service/front-desk/BedStatusService";
import ServiceSetupService from "../../service/setup/ServiceSetupService";
import EmployeeSetupService from "../../service/setup/EmployeeSetupService";
import ReferredBySetupService from "../../service/setup/ReferredBySetupService";
import Validation from "../../scripts/validation";
import IPDReportService from "../../service/report/IPDReportService";
import moment from "moment";
import GlobalSettingsService from "../../service/security/GlobalSettingsService";

export default {
  data() {
    return {
      bedListDialog: false,
      dataList: false,
      isContactExist: "",
      isValid: "",
      isEmailValid: "",
      isExist: "",
      bloodGroupList: [],
      genderList: [],
      departmentList: [],
      doctorList: [],
      buildingList: [],
      wordCabinCategoryList: [],
      wordCabinTypeList: [],
      wordCabinList: [],
      bedList: [],
      bedStatusList: [],
      bedStatus: {},
      layout: 'grid',

      referredByList: [],
      admission: {
        cdadmission_date: moment(String(new Date())).format(
          "MM/DD/YYYY"
        ),
        patient_id: "",
        aPatient: {
          age_year: "0",
          age_month: "0",
          age_day: "0",
        },
        aAdmissionBedDetails: {},
        aAdmissionServiceDetails: {},
        aPatientInvoice: {},
      },

      submitted: false,
      globalSetting: {},
    };
  },
  created() {
    this.admissionService = new AdmissionService();
    this.patientRegistrationService = new PatientRegistrationService();
    this.commonService = new CommonService();
    this.doctorSetupService = new DoctorSetupService();
    this.consultancyDepartmentSetupService = new ConsultancyDepartmentSetupService();
    this.buildingSetupService = new BuildingSetupService();
    this.wordCabinCategorySetupService = new WordCabinCategorySetupService();
    this.wordCabinTypeSetupService = new WordCabinTypeSetupService();
    this.wordCabinSetupService = new WordCabinSetupService();
    this.bedSetupService = new BedSetupService();
    this.bedStatusService = new BedStatusService();
    this.serviceSetupService = new ServiceSetupService();
    this.employeeSetupService = new EmployeeSetupService();
    this.referredBySetupService = new ReferredBySetupService();
    this.validation = new Validation();
    this.iPDReportService = new IPDReportService();
    this.globalSettingsService = new GlobalSettingsService();

    this.commonService
      .GetAllGender()
      .then((data) => (this.genderList = data.data));

    this.commonService
      .GetAllBloodGroup()
      .then((data) => (this.bloodGroupList = data.data));

    this.consultancyDepartmentSetupService
      .getActiveCombo()
      .then((data) => (this.departmentList = data.data));

    this.buildingSetupService
      .getActiveCombo()
      .then((data) => (this.buildingList = data.data));

    this.wordCabinCategorySetupService
      .getActiveCombo()
      .then((data) => (this.wordCabinCategoryList = data.data));

    this.wordCabinTypeSetupService
      .getActiveCombo()
      .then((data) => (this.wordCabinTypeList = data.data));

    this.wordCabinSetupService
      .getAll()
      .then((data) => (this.wordCabinList = data.data));

    this.bedSetupService
      .getAll()
      .then((data) => (this.bedList = data.data));

    this.serviceSetupService
      .getById('1')
      .then((data) => this.getServiceFormatted(data.data));

    this.referredBySetupService
      .getAll()
      .then((data) => this.mergeReferredByInfo(data.data));

    this.doctorSetupService
      .getAll()
      .then((data) => this.mergeDoctorInfo(data.data));

    this.employeeSetupService
      .getAll()
      .then((data) => this.mergeEmployeeInfo(data.data));


    this.globalSettingsService
      .getBySettingName("Admission Payment Express")
      .then((data) => (this.globalSetting = data.data));


    var transferPatientRegNo = localStorage.getItem('transferPatientRegNo');
    if (transferPatientRegNo) {
      this.admission.contactOrRegNo = transferPatientRegNo;
      localStorage.removeItem('transferPatientRegNo');
      this.findPatient();
    }
  },

  methods: {
    printAdmissionToken(Id) {
      this.iPDReportService.GetAdmissionToken(Id, this).then((data) => (this.commonService.Downloadfile(data.data)));
    },
    getWordCabin() {
      if (!this.bedStatus.building_id) {
        return;
      } else if (!this.bedStatus.word_cabin_type_id) {
        return;
      } else if (!this.bedStatus.word_cabin_category_id) {
        return;
      } else {
        return this.wordCabinList.filter(function (el) {
          return (
            el.building_id == this.bedStatus.building_id && el.word_cabin_type_id == this.bedStatus.word_cabin_type_id && el.word_cabin_category_id == this.bedStatus.word_cabin_category_id);
        }, this);
      }
    },
    showBed() {
      this.submitted = true;
      if (!this.bedStatus.word_cabin_id) {
        return;
      }
      else {
        this.bedStatusService
          .getDynamic(
            this.bedStatus.word_cabin_id, this
          )
          .then((data) => this.getFormattedBedInfo(data.data));
      }
    },
    getFormattedBedInfo(data) {
      this.bedStatusList = data;
      this.bedCount = this.bedStatusList.length;

      var admitted = this.bedStatusList.filter(function (el) {
        return (
          el.status == "Admitted" && el.active == 'True');
      }, this);
      this.admittedCount = admitted.length;

      var available = this.bedStatusList.filter(function (el) {
        return (
          el.status == "Available" && el.active == 'True');
      }, this);
      this.availableCount = available.length;

      var inactive = this.bedStatusList.filter(function (el) {
        return (
          el.active == 'False');
      }, this);
      this.inactiveCount = inactive.length;

      this.dataList = true;
      this.submitted = false;
    },
    setBedInfo(data) {
      if (data.active == 'True' && data.status == "Available") {
        this.admission.aAdmissionBedDetails.building_name = data.building_name;
        this.admission.aAdmissionBedDetails.word_cabin_category_name = data.word_cabin_category_name;
        this.admission.aAdmissionBedDetails.word_cabin_name = data.word_cabin_name;
        this.admission.aAdmissionBedDetails.bed_id = data.bed_id;
        this.admission.aAdmissionBedDetails.mrp = data.charge;

        this.hideDialog();
      }
    },
    getServiceFormatted(data) {
      this.admission.aAdmissionServiceDetails.service_id = data.service_id;
      this.admission.aAdmissionServiceDetails.charge = data.service_charge;
      this.admission.aAdmissionServiceDetails.discount = data.discount;

      //this.serviceList.push(data);
    },
    hideDialog() {
      this.bedListDialog = false;
    },
    showBedListDialog() {
      this.bedListDialog = true;
      this.showBed();
    },
    reset() {
      this.isValid = "";
      this.isEmailValid = "";
      this.isExist = "";
      this.isContactExist = "";
      this.admission = {
        cdadmission_date: moment(String(new Date())).format(
          "MM/DD/YYYY"
        ),
        patient_id: "",
        aPatient: {
          age_year: "0",
          age_month: "0",
          age_day: "0",
        },
        aAdmissionBedDetails: {},
        aAdmissionServiceDetails: {},
        aPatientInvoice: {},
      };
      this.submitted = false;
      localStorage.removeItem('transferPatientRegNo');
    },

    filterDoctorsDepartmentWise() {
      return this.doctorList.filter(function (el) {
        return el.department_id === this.admission.department_id;
      }, this);
    },
    mergeReferredByInfo(data) {
      let filterData = data.filter(function (el) {
        return el.active == true;
      }, this);
      filterData.forEach((element) => {
        this.referredByList.push({
          value: element.registration_no,
          text: element.manual_code + "-" + element.registration_no + "-" + element.reffered_by_name + "(Referred By)",
        });
      });
    },
    mergeDoctorInfo(data) {
      //this.doctorList = data;
      data.forEach((element) => {
        this.referredByList.push({
          value: element.doc_no,
          text: element.doc_no + "-" + element.doctor_name + "(Doctor)",
        });
      });

      data.forEach((element) => {
        var obj = element;
        obj.doctor_degree = "";
        obj.lstConsultantDegree.forEach((el) => {
          obj.doctor_degree += el.consultant_degree_name + ". ";
        });

        obj.doctor_name = element.doc_no + " - (" + obj.doctor_degree + ") - " + element.doctor_name;
        this.doctorList.push(obj);
      });
    },
    mergeEmployeeInfo(data) {
      let filterData = data.filter(function (el) {
        return el.active == true;
      }, this);
      filterData.forEach((element) => {
        this.referredByList.push({
          value: element.emp_no,
          text: element.emp_no + "-" + element.first_name + "(Employee)",
        });
      });
    },
    getFormatted(data) {
      this.admission.aPatient = data;
      if (!this.admission.aPatient.patient_id) {
        this.isExist = "Patient not Exist!";
        this.admission = {
          patient_id: "",
          aPatient: {},
        };
      } else {
        this.isExist = "";

        this.admission.patient_id =
          this.admission.aPatient.patient_id;
        this.admission.aPatient.cddob = moment(
          String(this.admission.aPatient.dob)
        ).format("YYYY-MM-DD");
      }
    },
    findPatient() {
      this.patientRegistrationService
        .getByContactOrRegNo(this.admission.contactOrRegNo, this)
        .then((data) => this.getFormatted(data.data));
    },
    isExistContact() {
      if (
        this.admission.aPatient.contact_no.match(/^(01)([0-9]{9})$/)
      ) {
        this.patientRegistrationService
          .getByContactOrRegNo(this.admission.aPatient.contact_no, this)
          .then((data) => this.getContact(data.data));
      } else {
        this.isContactExist =
          "Contact number must start with 01 and the required length is 11";
      }
    },
    getContact(data) {
      if (data.contact_no) {
        this.isContactExist = "Duplicate phone number!";
      } else {
        this.isContactExist = "";
      }
    },

    async add() {
      this.submitted = true;
      try {
        if (!this.admission.aPatient.first_name) {
          return;
        } else if (!this.admission.aPatient.age_year) {
          return;
        } else if (!this.admission.aPatient.age_month) {
          return;
        } else if (!this.admission.aPatient.age_day) {
          return;
        } else if (!this.admission.aPatient.contact_no) {
          return;
        } else if (!this.admission.department_id) {
          return;
        } else if (!this.admission.doctor_id) {
          return;
        } else if (!this.admission.aAdmissionBedDetails.bed_id) {
          return;
        } else if (!this.admission.cdadmission_date) {
          return;
        } else if (this.isContactExist || this.isScheduleExist) {
          return;
        } else {
          this.admission.admission_date = moment(
            String(this.admission.cdadmission_date)
          ).format("YYYY-MM-DD");

          var currentDate = new Date();
          var dob = new Date(currentDate.getFullYear() - this.admission.aPatient.age_year, currentDate.getMonth() - this.admission.aPatient.age_month, currentDate.getDate() - this.admission.aPatient.age_day);
          this.admission.aPatient.dob = moment(
            String(dob)
          ).format("YYYY-MM-DD");

          if (!this.admission.patient_id) {
            let result = await this.patientRegistrationService.add(
              this.admission.aPatient, this
            );
            if (result.status === 200) {
              // this.$toast.add({
              //   severity: "success",
              //   summary: "Success",
              //   detail: "Saved Successfully",
              //   life: 10000,
              // });
              this.admission.patient_id = result.data.toString();
            }
            else {
              this.$toast.add({
                severity: "error",
                summary: "Failed",
                detail: result.res,
                life: 10000,
              });
              return;
            }
          }

          let result = await this.admissionService.add(
            this.admission, this
          );

          if (result.status === 200) {
            this.$toast.add({
              severity: "success",
              summary: "Success",
              detail: "Saved Successfully",
              life: 10000,
            });

            if (this.globalSetting.setting_value == "Yes") {
              this.confirmPay(result.data);
            } else {
              this.reset();
              this.printAdmissionToken(result.data);
            }

            // var transferPatientRegNo = localStorage.getItem('transferPatientRegNo');
            // if (transferPatientRegNo) {
            //   localStorage.removeItem('transferPatientRegNo');
            //   this.$router.push("/patient-reg-treatment");
            // }
            //this.reset();
          } else if (result.status === 409) {
            this.$toast.add({
              severity: "error",
              summary: "Error",
              detail: result.data,
              life: 10000,
            });
          } else {
            this.$toast.add({
              severity: "error",
              summary: "Failed",
              detail: result.res,
              life: 10000,
            });
          }
        }
      } catch (error) {
        this.$toast.add({
          severity: "error",
          summary: "Failed",
          detail: error,
          life: 10000,
        });
      }
    },
    confirmPay(data) {
      this.$confirm.require({
        message: 'Are you sure you want to pay now?',
        header: 'Confirmation',
        icon: 'pi pi-exclamation-triangle',
        accept: () => {
          localStorage.setItem('admission_id_for_payment', data);
          this.$router.push("/admission-payment-collection");
        },
        reject: () => {
          this.printAdmissionToken(data);
          this.reset();
        },
      });
    }

  },
};
